	switch ((code + '').toUpperCase()) {
		case 'F':
			fhirCode = org.hl7.fhir.dstu3.model.codesystems.AdministrativeGender.FEMALE;
			break;
		case 'M':
			fhirCode = org.hl7.fhir.dstu3.model.codesystems.AdministrativeGender.MALE;
			break;
		case 'O':
		case 'A':
		case 'N':
