/**
	Converts an HL7 v2.x formatted timestamp into the correct format for FHIR dateTime.

	HL7 format expected: yyyyMMddHHmmssZZ
	FHIR format: yyyy-MM-dd'T'HH:mm:ssZZ

	@param {String} date - The HL7 v2.x timestamp string
	@return {String} The converted FHIR timestamp string
*/
function convertToFhirTimestamp(date) {
	if (!date) {
		return '';
	}

	var patterns = [
		"yyyyMMddHHmmss.SSSZZ",
		"yyyyMMddHHmmss.SSSZ",
		"yyyyMMddHHmmss.SSS",
		"yyyyMMddHHmmssZZ",
		"yyyyMMddHHmmssZ",
		"yyyyMMddHHmmss",
		"yyyyMMddHHmm",
		"yyyyMMddHH",
		"yyyyMMdd"
	];

	var instant;
	for each (pattern in patterns) {
		try {
			instant = org.joda.time.format.DateTimeFormat.forPattern(pattern).parseMillis(new String(date));
			break;
		} catch(e) {}
	}

	if (instant) {
		var outpattern = arguments.length >= 2 ? arguments[1] : "yyyy-MM-dd'T'HH:mm:ssZZ";
		try {
			return org.joda.time.format.DateTimeFormat.forPattern(outpattern).print(instant);
		} catch (e) {}
	}

	return date;
}

/**
	Converts an HL7 v2.x yes/no indicator code to a boolean value. If the code is null or an empty string,
	false will be returned.

	@param {String} code - The HL7 yes/no indicator (Table 0136)
	@return {Boolean} True/false
*/
function convertYesNoIndicator(code) {
	return code == 'Y';
}

/**
	Convert an HL7 to XML.

	@param {String} inORM - the hl7 as string
	@return {XML} return XML (HL7)
*/
function HL7toXML(inORM) {
	//
	//at this point we have a string not HL7 object. 
	//

	//
	//convert PIPE delimited string (HL7 ORM) to XML for easy parsing...
	//
	logger.debug("Converting to XML");
	var ORMtoXML = "";	
	var serializationProperties = SerializerFactory.getDefaultSerializationProperties('HL7V2');
	serializationProperties.put('convertLineBreaks',true);
	serializationProperties.put('useStrictParser',false);
	serializationProperties.put('useStrictValidation',false);
	serializationProperties.put('stripNamespaces',false);
	serializationProperties.put('handleSubcomponents',true);
	serializationProperties.put('handleRepetitions',true);

	// Setting the default namespace is required when using the strict parser
	//default xml namespace = new Namespace('urn:hl7-org:v2xml');

	var ORMtoXML = SerializerFactory.getSerializer('HL7V2', serializationProperties, null).toXML(inORM);  
	var newXML = new XML(ORMtoXML);

	return newXML;
}

