				{
					objCode.text = obrSeg['OBR.4']['OBR.4.2'];
				}


				if (obrSeg['OBR.4']['OBR.4.3'].toString().trim() != "")
				{
					var arrCodeExtension 	= new Array();
					arrCodeExtension[0] 	= {"url":"https://api.excelleris.com/1.0/eorder/diagnosticorder-item-code-mnemonic","valueString":obrSeg['OBR.4']['OBR.4.3']};
					objCode.coding 		=  arrCodeCoding ;
					objCode.extension 		= arrCodeExtension;

					//arrCodeExtension[0].valueString = obrSeg['OBR.4']['OBR.4.3'];//mnemonic ex: NA
				}


				//put code in main object
				objMain.code = objCode;

			} //end of code/obr.4

			logger.debug("OBR.27.6 : " +  obrSeg['OBR.27']['OBR.27.6']);

			//create  diagnosticorder-item-priority extension
			if (obrSeg['OBR.27']['OBR.27.6'].toString().trim() != "")
			{
				arrExtension = GetFHIRItemTestPriority(obrSeg['OBR.27']['OBR.27.6'], arrExtension);
			}

			//create eorder-item-contactpoint - only skip if 'OBR.27.6 = routine
			if (obrSeg['OBR.27']['OBR.27.6'].toString().trim().toUpperCase() != "R")
			{
				//create eorder-item-contactpoint - only skip if 'OBR.27.6 = routine
				var zbrSeg = getSegmentsAfter(inHL7,obrSeg,'ZBR',false, ['BLG']);
				logger.debug("zbrSeg after OBR: " +  zbrSeg[0]);
				if (typeof(zbrSeg[0]['ZNT.16']) != "undefined")
				{
					arrExtension = GetFHIRItemTestContactPoint(zbrSeg[0], arrExtension);
				}

			}

			cntLoop++;
		}



		DiagOrderRes.item = arrItem;


	} //end of ORC 

	//add extensions
	DiagOrderRes.extension = arrExtensions;
logger.debug(JSON.stringify(inBundle));
	var FhirResourceUUID = inBundle['entry'].getByResourceType("Patient");
	//create subject object - patient UUID 
	if (FhirResourceUUID)
	{
		var objSubject       = new Object();
		objSubject.reference =  FhirResourceUUID.fullUrl;
		DiagOrderRes.subject = objSubject;
	}

	//create order object
	FhirResourceUUID = inBundle['entry'].getByResourceType("Practitioner");
	if (FhirResourceUUID)
	{
		var objOrderer 	 = new Object();
		objOrderer.reference = FhirResourceUUID.fullUrl;
		DiagOrderRes.orderer = objOrderer ;
	}

	//create encounter object
	FhirResourceUUID = inBundle['entry'].getByResourceType("Encounter");
	if (FhirResourceUUID)
	{
		var objEncounter 	    = new Object();
		objEncounter.reference  = FhirResourceUUID.fullUrl;
		DiagOrderRes.encounter  = objEncounter;
	}

	//priority string
	if (inHL7['ZBR']['ZBR.14']['ZBR.14.1'].toString().trim() != "")
	{
		DiagOrderRes.priority =  GetFHIRTestPriority(inHL7['ZBR']['ZBR.14']['ZBR.14.1']);
	}
	else
	{
		DiagOrderRes.priority = GetFHIRTestPriority("R");
	}

	//
	//Get Accession PID-Level Notes create note array
	//
	{
	var pidNTEs = getSegmentsAfter(inHL7,inHL7['PID'],'NTE',false, ['PV1']);
	var arrNote = new Array();

	for each (notePid in pidNTEs)
	{
		//logger.debug(".......PID NTES..." +  JSON.stringify(notePid));

		//create valueAnnotation
		var valueAnnotation = new Object();

		//create extension array
		var valueExtension = new Array();
		valueExtension.push({"url":"https://api.excelleris.com/1.0/eorder/StructureDefinition/timestamp", "valueString":""});
		valueExtension.push({"url":"https://api.excelleris.com/1.0/eorder/StructureDefinition/NoteType", "valueCode":notePid['NTE.4']['NTE.4.1']});

		var segZNT = getSegmentsAfter(inHL7,notePid,'ZNT',false, ['NTE']);
		logger.debug("supposed to be ZNT:" + segZNT);
		if (typeof(segZNT[0]['ZNT.4']) != "undefined"  ){
			logger.debug("adding ZNT:" + segZNT[0]['ZNT.4']['ZNT.4.1']);
			valueExtension.push({"url":"https://api.excelleris.com/1.0/eorder/StructureDefinition/CannedComment", "valueCode":segZNT[0]['ZNT.4']['ZNT.4.1']});
		}

		//create time
		var valueTime = "";

		//create text
		var valueText = notePid['NTE.3']['NTE.3.1']; 
		logger.debug(notePid['NTE.1'] +":" + valueText);
		//FIXME: could be double quote here...parsing below removes backslash. (ex: "Sent )

		//build authorReference
		var authorReference = {};
		authorReference.reference = "person/id";
		authorReference.display = "User that made the note";

		//populate valueAnnotation with order level notes
		valueAnnotation.extension = valueExtension;
		valueAnnotation.authorReference = authorReference;
		valueAnnotation.time = valueTime;
		valueAnnotation.text = valueText;

		//
		//create test note item extension
		arrNote[arrNote.length] = valueAnnotation;

	}

		DiagOrderRes.note = arrNote;

	} //end of note array creation


	//return object with data?
	if (!isObjectEmpty(DiagOrderRes))
	{
		var DiagnosticResource = { "resource" : DiagOrderRes };	
		logger.debug("Diagnostic Order NEW: " + JSON.stringify(DiagnosticResource));
		return DiagnosticResource;
	}
	else
	{
		return undefined;
	}


}

/**
	Creates a FHIR Encounter resource for LifeLabs.

	@param {json} inBundle - outbound message template (json)
	@param {Any} inHL7 - HL7 response from IntRLab
	@return {json} return Location resource or undefined if no data. 
*/
function CreateEncounterResource(inBundle, inHL7) 
{

	//create resource
	var EncounterRes 	  = new Object();

	//resource not used yet
/*	if (inHL7['seg'].toString().trim() != "")
	{
		//create resource
		var EncounterRes = new Object();

		//create resourceType
		EncounterRes.resourceType = "Encounter";

		//create location item inside location array
		var locationItem = new Object();
		locationItem.reference = "here";

		//create extension array
		EncounterRes.location = [
			{"location": locationItem }
		];
	}

*/		

	if (!isObjectEmpty(EncounterRes))
	{
		var EncounterRes = "{ \"resource\" " + ":" + JSON.stringify(EncounterRes) + "}";
		return JSON.parse(EncounterRes);

	}else
	{
		return undefined;
	}


}

/**
	LifeLabs function to create FHIR from HL7.

	@param {Any} inFHIR - 
	@param {Any} inHL7 - 
	@return {String} return description
*/
function CreateFHIRfromHL7(inFHIR, inHL7)
{
	logger.debug("in CreateFHIRfromHL7. inFHIR: " + JSON.stringify(inFHIR));
	//logger.debug("in CreateFHIRfromHL7. inHL7: " + SerializerFactory.getSerializer('HL7V2').fromXML(inHL7));

	//resources used in this bundle
	var PatientRes 		= new Array();
	var PractitionerRes 	= new Array();
	var EncounterRes		= new Array();
	var LocationRes		= new Array();
	var DiagnosticOrderRes	= new Array();

	//uuids
	var PatientUUID 		= UUIDGenerator.getUUID();
	var PractitionerUUID 	= UUIDGenerator.getUUID();
	var LocationUUID 		= UUIDGenerator.getUUID();
	var DiagnosticOrderUUID  = UUIDGenerator.getUUID();

	//count for total tests in hl7
	var cntTests			= inHL7['ORC'].length();

	try
	{

	//
	//Create Patient Resource
	//
	logger.debug("CreateFHIRfromHL7.CreatePatientResourceForBundle");
	PatientRes = CreatePatientResourceForBundle(inFHIR, inHL7);
	if (PatientRes != undefined)
	{
		logger.debug("Returned Patient Resource:" + JSON.stringify(PatientRes));
		inFHIR = SetResourceInBundle(inFHIR, PatientRes, PatientUUID);
	}

	//
	//Create Location Resource
	//
	logger.debug("CreateFHIRfromHL7.CreateLocationResource");
	LocationRes = CreateLocationResource(inFHIR, inHL7);
	if (LocationRes != undefined)
	{
		inFHIR = SetResourceInBundle(inFHIR, LocationRes, LocationUUID);
	}

	//
	//Create Encounter Resource
	//
	logger.debug("CreateFHIRfromHL7.CreateEncounterResource");
	EncounterRes = CreateEncounterResource(inFHIR, inHL7);
	if (EncounterRes != undefined)
	{
		inFHIR = SetResourceInBundle(inFHIR, EncounterRes, "ENCOUNTER");
	}

	//
	//Create Practitioner Resource
	//
	logger.debug("CreateFHIRfromHL7.CreatePractitionerResource");
	PractitionerRes = CreatePractitionerResource(inFHIR, inHL7);
	if (PractitionerRes != undefined)
	{
		inFHIR = SetResourceInBundle(inFHIR, PractitionerRes, PractitionerUUID);
	}

	//
	//Create DiagnosticOrder Resource
	//
	logger.debug("CreateFHIRfromHL7.CreateDiagnosticResource");
	var DiagnosticOrderRes = CreateDiagnosticResource(inFHIR, inHL7);
	if (DiagnosticOrderRes != undefined)
	{
		logger.debug("Returned Diagnostic Order:" + JSON.stringify(DiagnosticOrderRes));
		inFHIR = SetResourceInBundle(inFHIR, DiagnosticOrderRes, DiagnosticOrderUUID);
	}



	logger.debug("In  CreateFHIRfromHL7...returning new FHIR Bundle: " + JSON.stringify(inFHIR));
	}
	catch(err)
	{
		logger.error("In CreateFHIRfromHL7 Code Template: " + err);
		throw err;
	}

	return  inFHIR;

}

/**
	Creates a FHIR Location resource for LifeLabs.

	@param {json} inBundle - outbound message template (json)
	@param {Any} inHL7 - HL7 response from IntRLab
	@return {json,boolean} return Location resource or false no data message. 
*/
function CreateLocationResource(inBundle, inHL7) 
{
	//logger.debug("In  CreateLocationResource");

	//create resource
	var LocationResource 	  = new Object();

	if (inHL7['PV1']['PV1.3']['PV1.3.1'].toString().trim() != "")
	{	
		//create resourceType
		LocationResource.resourceType = "Location";

		//create identifier array
		LocationResource.identifier = [{"value": ""}];
		LocationResource.identifier[0].value = inHL7['PV1']['PV1.3']['PV1.3.1'];

	}

	//not using yet
	/*if (inHL7['segment'].toString().trim() != "")
	{
		//create extension array
		LocationRes.extension = [
			{"url": ""},
			{"valueString": ""}
		];

		//populate exension
		LocationRes.extension[0].url = "https://api.excelleris.com/1.0/eorder/location-group";
		LocationRes.extension[0].valueString = inHL7['PV1']['PV1.3'];
	}*/

	if (!isObjectEmpty(LocationResource))
	{
		var LocationResource = "{ \"resource\" " + ":" + JSON.stringify(LocationResource) + "}";
		//logger.debug("Encounter NEW: " + JSON.stringify(JSON.parse(LocationResource)));	
		return JSON.parse(LocationResource);
	}
	else
	{
		return undefined;
	}

}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {xml} xml - xml version of the hl7 response from IntRLab
	@param {json} tmp - outbound message template (json)
	@return {String}  - updated Stringified JSON object
*/
// Changes XML to JSON
//xml = xml version of the hl7 response from IntRLab
//tmp = outbound message template (json)

function CreateFHIRBundle(xml, tmp) 
{

try
{
	//logger.info("xmlToJson...");
	//tmp = AddNewOrderResourceToFHIRBundle(tmp, 4);
	//return;

	//1. Is this single ORM or multiple?
	var isMultiORM = HasMultipleORMsMessages(xml);
	//logger.info("Do We Have Multiple ORMs? : " + isMultiORM) ;
	//logger.info("Full HL7 XML: " + xml);

	//
	//2. Get HL7s individually...
	//
	var lXML = xml;
	//if I don't remove now - it will cause issues later.
	lXML = lXML.toString().replace("</HL7Message>","");
	var arrHL7 = lXML.toString().split("<MSH>");

	//set counters
	var hl7Count = arrHL7.length - 1;
	var ormCount = hl7Count - 1;
	var loop = 0;

	//counts are always correct.
	logger.info("HL7 message count: " + hl7Count);
	logger.info("ORM message count: " + ormCount);

	//in the mirth JSON template, we add new order entries to template at position 3 or tmp['entry'][3]
	var orderCountEntryPos = 2;

	//
	//loop through entire IntRLab response
	//
	for each (hl7Msg in arrHL7)
	{
		if (loop == 1)
		{
			//loop 1 is the main MSH that holds ORMs
			//logger.info("MAIN MSH holding ORMs: " + hl7Msg.toString());	
		} 
		else if (loop >= 2)
		{
			//
			//loop 2 and greater are all the ORM messages
			//

			//
			//this is the individual ORM (hl7Msg.toString)
			//

			//we split earlier above, so put tags back in to make this a valid XML
			var curHL7Msg = "<HL7Msg><MSH>" + hl7Msg.toString() + "</HL7Msg>"; //valid XML
			//logger.info("CURRENT ORM IN LOOP" + curHL7Msg);

			//
			//this is the new individual ORM as XML format (curHL7.toString())
			//
			var curHL7 = new XML(curHL7Msg);

			//
			//is this multiple ORMs
			//

			if (isMultiORM)
			{
				//
				//add order resources to template (at position 3 [3])
				//
				//orderCountEntryPos++;
			}

			//logger.info("orderCountEntryPos: " + orderCountEntryPos);
			//logger.info("ormCount: " + ormCount);

	 		//should we clean up order bundle template?
	 		if (orderCountEntryPos > ormCount)
	 		{
	 			//logger.info("FOUND the last ORDER!");
	 			//logger.info("creating patient resource for bundle");
				tmp['entry'][0]['resource'] =  CreatePatientResource(curHL7, tmp['entry'][0]['resource'], true, true);
	 		}
	 		else
	 		{
	 			//logger.info("creating patient resource for bundle");
				//clean up order bundle template
				tmp['entry'][0]['resource'] =  CreatePatientResource(curHL7, tmp['entry'][0]['resource'], true, false);
	 		}

			logger.info("creating practitioner resource for bundle");
			tmp = CreatePractitionerResource(tmp, curHL7);

			//Order FHIR resource are multiple resources
			//
			//Create Diagnostic Order for bundle
			//

			//loop through ORM message and create order resource...
			tmp = CreateOrderResource(curHL7, ormCount, orderCountEntryPos, tmp);
			orderCountEntryPos++;
		}
		loop++;
	}
	return JSON.stringify(tmp);

}
catch(err)
{
	logger.error("Error in xmlToJson(): " + err.message);
	//throw err;
}
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {Any} curHL7 - An individual HL7 ORM from IntRLab response.
	@param {Any} ormCount - The total number of HL7 ORMs in the IntRlab response.
	@param {Any} orderCountEntryPos - the array position to put the new order resource (starts at position #2).
	@param {Any} inTmp - JSON template.
	@return {String} return description
*/
//For Order Bunlde, the Order data is in entry[2], entry[3], etc (depending on how many ORMs returned)
//ORC, OBR, ZBR are required - they make up IntRlab ORM message

//curHL7 = An individual HL7 ORM from IntRLab response.
//ormCount =  The total number of HL7 ORMs in the IntRlab response.
//orderCountEntryPos = the array position to put the new order resource (starts at position #2).
//inTmp =  json template.
function CreateOrderResource(curHL7, ormCount, orderCountEntryPos, inTmp)
{	
	//logger.info("--------BEG CreateOrderResource----------");

	//
	//TEST RESULT: ORC, OBR, OBX,  ZBX
	//
	//ORC.4 - Order ID/Report ID
	//OBR.2 - Test Request ID
	//OBX.3 - Test Result Code
	//OBX.4 - Observervaion SUB-ID
	//ZBX.1 - Test Result Release Date/Time

	//set the OBR segment counter
	var obrCount = 1;

	//loop through ORM message and find segments...
	for each (itemSeg in curHL7.children())
	{

		//logger.info("BEG LOOP: orderCountEntryPos: " + orderCountEntryPos);
		//logger.info("BEG LOOP: group: " + group);
		//create order resource...
		var curSegment = new XML(itemSeg);


		//logger.info("Create Order Resource in entry[" + orderCountEntryPos + "]");

		//make sure there's an entry in the template for the order resource
		if (orderCountEntryPos >= 3)
		{
			//logger.info("Add entry[] to template in pos: " + orderCountEntryPos);
			//will have ORC, OBR, ZBR
			if (curSegment.toString().indexOf("<OBR>") > -1)
			{
				inTmp = AddNewOrderResourceToFHIRBundle(inTmp, orderCountEntryPos, curSegment, curHL7);
			}
		}
		else
		{
		//Is OBR?
		if (curSegment.toString().indexOf("<OBR>") > -1)
		{
			var itemSeg = new XML(curSegment);
			//logger.info("FOUND OBR segment itemSeg: " + itemSeg);

					//
					//A test request in IntRLab consists of an ORC-OBR-ZBR segment sequence
					//
					//if (orderCountEntryPos <= ormCount)
					//{    


							//always one patient resource
							inTmp['entry'][orderCountEntryPos]['resource']['subject']['reference'] = "Patient/"+curHL7['PID']['PID.3']['PID.3.1'];
							inTmp['entry'][orderCountEntryPos]['resource']['subject']['display']   = curHL7['PID']['PID.5']['PID.5.2'];

							//build fullurl
							inTmp['entry'][orderCountEntryPos]['fullUrl'] = "/diagnosticorder/"+itemSeg['OBR.2']['OBR.2.1']


							//ORDERING PRACTITIONER ADDRESS IS ORC.24
							//ORC|NW|||20060810.000001^^2.16.840.1.113883.3.59.3:9999^ISO|||||200608101452000400||||||||||||AnytownGeneralHospital^^^^^&2.16.840.1.113883.3.59.3:9999&ISO|123MainStreet^^Anytown^ON^M5W1E6^CAN^B||500 Main Street^3rd floor^Anytown^ON^M5W 5E5^CAN^B<CR> 

							//ORC-12 / OBR-16 / RXO-14 / RXE-13 / PRT-5 (PRT-4-Participation='OP')
							inTmp['entry'][orderCountEntryPos]['resource']['orderer']['reference'] = "Practitioner/"+itemSeg['OBR.16']['OBR.16.1'];
							//
							//each OBR goes into an item...
							//
							inTmp['entry'][orderCountEntryPos]['resource']['item'][0]['code']['coding'][0]['code'] = itemSeg['OBR.4']['OBR.4.1'];
							//test request code - OBR 4.1
							//test request desc - OBR 4.2
							//test request type - OBR 4.3 (table 9901 olis test request nomenclature)
							inTmp['entry'][orderCountEntryPos]['resource']['item'][0]['extension'][0]['url'] = "diagnosticorder-item-testrequestcode";
							inTmp['entry'][orderCountEntryPos]['resource']['item'][0]['extension'][0]['valueCodeableConcept']['coding'][0]['code'] = itemSeg['OBR.4']['OBR.4.1'];
							inTmp['entry'][orderCountEntryPos]['resource']['item'][0]['extension'][1]['url'] = "diagnosticorder-item-testrequestdesc"
							inTmp['entry'][orderCountEntryPos]['resource']['item'][0]['extension'][1]['valueString'] = itemSeg['OBR.4']['OBR.4.2'];
							inTmp['entry'][orderCountEntryPos]['resource']['item'][0]['extension'][2]['url'] = "diagnosticorder-item-testrequestcodetype";
							inTmp['entry'][orderCountEntryPos]['resource']['item'][0]['extension'][2]['valueString'] = itemSeg['OBR.4']['OBR.4.3'];
							//item comment [3]
							inTmp['entry'][orderCountEntryPos]['resource']['item'][0]['extension'][3]['valueString'] = "This is and item comment for " + itemSeg['OBR.4']['OBR.4.1'] + " " + itemSeg['OBR.4']['OBR.4.2'];


						obrCount++;	
					//}
				}
		}
	}

	//logger.info("OBRCOUNT: " + obrCount);

	//logger.info("--------END CreateOrderResource----------");

	return inTmp;
}

//
function AddNewOrderResourceToFHIRBundle(inJson, inEntryArrayPos, curSeg, curHL7)
{
	//var inJsonString = JSON.stringify(inJson);
	//var inJsonObj    = JSON.parse(inJsonString);
	var inJsonObj    = inJson;

	//build ORDER with json	
	inJsonObj.entry[inEntryArrayPos] = {};
	inJsonObj.entry[inEntryArrayPos].fullUrl = "diagnosticorder/"+curSeg['OBR.2']['OBR.2.1'];

	//build resource object
	inJsonObj.entry[inEntryArrayPos].resource = {};
	inJsonObj.entry[inEntryArrayPos].resource.resourceType = "DiagnosticOrder";
	inJsonObj.entry[inEntryArrayPos].resource.id = curSeg['OBR.2']['OBR.2.1'];

	//build extension object under resource
	inJsonObj.entry[inEntryArrayPos].resource.extension = {};

	//build subject object under resource
	inJsonObj.entry[inEntryArrayPos].resource.subject = {};
	inJsonObj.entry[inEntryArrayPos].resource.subject.reference = "Patient/"+curHL7['PID']['PID.3']['PID.3.1'];
	inJsonObj.entry[inEntryArrayPos].resource.subject.display   = curHL7['PID']['PID.5']['PID.5.2']; 

	//build orderer object under resource
	inJsonObj.entry[inEntryArrayPos].resource.orderer = {};
	inJsonObj.entry[inEntryArrayPos].resource.orderer.reference = "Practitioner/"+curSeg['OBR.16']['OBR.16.1'];

	inJsonObj.entry[inEntryArrayPos].resource.status = "requested";
	inJsonObj.entry[inEntryArrayPos].resource.priority = "routine";

	//build item object under resource
	inJsonObj.entry[inEntryArrayPos].resource.item = [ {}, {} ];
	inJsonObj.entry[inEntryArrayPos].resource.item[0].extension = {};
	inJsonObj.entry[inEntryArrayPos].resource.item[1].extension = {};

	//build note under resource
	inJsonObj.entry[inEntryArrayPos].resource.note = [ {} ];
	inJsonObj.entry[inEntryArrayPos].resource.note[0].text = "This is my Diagnostic Order note for a new order at array position: " +inEntryArrayPos;

	return inJsonObj;
}


/*function CleanUpTemplate(inJson)
{
	var resourceXML = FhirUtil.jsonToXML(inJson);
	logger.info("turned template to XML: " + resourceXML);
}*/

/**
	Creates a FHIR patient resource or XML to JSON.

	@param {xml} xml - xml version of the hl7 response from IntRLab
	@param {json} tmp - outbound message template (json)
	@param {boolean} isBundle - True/False - is this a FHIR Bundle?
	@param {boolean} isLastOrder - True/False - is this the last order in the FHIR Bundle?
	@return {json} FHIR Patient Resource
*/
// Changes XML to JSON
//xml = xml version of the hl7 response from IntRLab
//tmp = outbound message template (json)
function CreatePatientResource(xml, tmp, isBundle, isLastOrder) 
{
	// populate tmp
	logger.info("In code template.  CreatePatientResource xml: " + xml);
	logger.info("In code template.  CreatePatientResource tmp: " + JSON.stringify(tmp));

	//build text
	tmp['text']['status'] = "generated";
	tmp['text']['div'] = "<div><p><b>Generated Narrative with Details</b></p><p><b>id</b>: f201</p><p><b>identifier</b>: BSN = 123456789 (OFFICIAL), BSN = 123456789 (OFFICIAL)</p><p><b>active</b>: true</p><p><b>name</b>: Roel(OFFICIAL)</p><p><b>telecom</b>: ph: +31612345678(MOBILE), ph: +31201234567(HOME)</p><p><b>gender</b>: male</p><p><b>birthDate</b>: 13/03/1960</p><p><b>deceased</b>: false</p><p><b>address</b>: Bos en Lommerplein 280 Amsterdam 1055RW NLD (HOME)</p><p><b>maritalStatus</b>: Legally married <span>(Details : {SNOMED CT code '36629006' = '36629006', given as 'Legally married'}; {http://hl7.org/fhir/v3/MaritalStatus code 'M' = 'Married)</span></p><p><b>multipleBirth</b>: false</p><p><b>photo</b>: </p><h3>Contacts</h3><table><tr><td>-</td><td><b>Relationship</b></td><td><b>Name</b></td><td><b>Telecom</b></td></tr><tr><td>*</td><td>Wife <span>(Details : {SNOMED CT code '127850001' = '127850001', given as 'Wife'}; {http://hl7.org/fhir/patient-contact-relationship code 'partner' = 'Partner)</span></td><td>Ariadne Bor-Jansma</td><td>ph: +31201234567(HOME)</td></tr></table><h3>Communications</h3><table><tr><td>-</td><td><b>Language</b></td><td><b>Preferred</b></td></tr><tr><td>*</td><td>Dutch <span>(Details : {urn:ietf:bcp:47 code 'nl-NL' = '??', given as 'Dutch'})</span></td><td>true</td></tr></table><p><b>managingOrganization</b>: <a>AUMC</a></p></div>";

	//build HCN
	tmp['identifier'][0]['type']['coding'][0]['code'] = xml['PID']['PID.3']['PID.3.1'];

	//build ACC
	//tmp['identifier'][1]['type']['coding'][0]['code']  = xml['PID']['PID.3']['PID.3.1'];

	//build name	
	for each (itemName in tmp['name'])//tmp.name
	{
		//logger.debug("----Looping through item name: "+ JSON.stringify(tmp['name']));
		if (itemName.use == "usual" &&
		    xml['PID']['PID.5']['PID.5.7'] == "U")
		{
			itemName.family[0] = xml['PID']['PID.5']['PID.5.1'];
			itemName.given[0]  = xml['PID']['PID.5']['PID.5.2'];
			itemName.given[1]  = (xml['PID']['PID.5']['PID.5.3']) != undefined ? xml['PID']['PID.5']['PID.5.3'] : "";
		}
		else if (itemName.use == "nickname")
		{
			itemName.family[0] = (xml['ZPD']['ZPD.7']['ZPD.7.1'] != undefined) ? xml['ZPD']['ZPD.7']['ZPD.7.1'] : "" ;
		}
	}

	//build telecom -
	var cntTelecom = 0;
	for each (item in xml['PID']['PID.13'])
	{
		tmp['telecom'][cntTelecom]['system'] = GetFHIRTelecomSystem(xml['PID']['PID.13'][cntTelecom]['PID.13.3']);
		tmp['telecom'][cntTelecom]['value']  = xml['PID']['PID.13'][cntTelecom]['PID.13.6'].toString() + xml['PID']['PID.13'][cntTelecom]['PID.13.7'].toString();
		tmp['telecom'][cntTelecom]['use']    = GetFHIRTelecomUse(xml['PID']['PID.13'][cntTelecom]['PID.13.2']);
		cntTelecom++;
	}

	//build gender
	tmp['gender'] = GetFHIRGender(xml['PID']['PID.8']['PID.8.1']);

	//build birthdate
	tmp['birthDate']	=  GetDOB(xml['PID']['PID.7']['PID.7.1'].toString());

	//build address
	var cntAddress = 0;	
	for each (item in xml['PID']['PID.11'])
	{
		tmp['address'][cntAddress]['use']        = GetFHIRAddressUse(xml['PID']['PID.11'][cntAddress]['PID.11.7']); //11.7	

		tmp['address'][cntAddress]['line']    = xml['PID']['PID.11'][cntAddress]['PID.11.1'].toString().split("\\.br\\"); //11.1;
		tmp['address'][cntAddress]['city']       = xml['PID']['PID.11'][cntAddress]['PID.11.3'].toString(); //11.3;
		tmp['address'][cntAddress]['postalCode'] = xml['PID']['PID.11'][cntAddress]['PID.11.5'].toString(); //11.5;
		tmp['address'][cntAddress]['country']    = xml['PID']['PID.11'][cntAddress]['PID.11.6'].toString(); //11.6;
		tmp['address'][cntAddress]['state']      = xml['PID']['PID.11'][cntAddress]['PID.11.4'].toString(); //11.4;
		cntAddress++;
	}

	//build deceased
	tmp['deceasedBoolean']  = GetFHIRDeathIndicator(xml['PID']['PID.30']['PID.30.1']);

	//
	//======================
	//Convert NTE-ZNT to FHIR (valueAnnotation object)
	//Create patient notes for patient resource
	{
	var patientNotesCount = xml['NTE'].length();
	logger.debug("Patient Notes from HL7: " +  patientNotesCount);

	//
	//build the extension json object on the fly to accomodate numerous NTE segments (notes)
	//

	//
	//build extension array
	//
	var extensionList = "";
	for (i = 1; i <= patientNotesCount; i++)
	{
	   if (i < patientNotesCount)
	   {
	     extensionList = extensionList +  "{\"url\":\"\", \"valueAnnotation\":{}},";	       
	   }
	   else if (i == patientNotesCount)
	   {
	     extensionList = extensionList +  "{\"url\":\"\", \"valueAnnotation\":{}}";
	   }
	}
	var extensions = "[" + extensionList + "]";
	extensions = JSON.parse(extensions);



	//
	//build authorReference
	//
	var authorReference = {};
	authorReference.reference = "person/id";
	authorReference.display = "User that made the note";



	//
	//loop - populate all
	//
	for (i = 0; i < patientNotesCount; i++)
	{
		//
		//build valueAnnotation
		//
		valueAnnotation = {};

		//
		//populate valueAnnotation
		//
		valueAnnotation.authorReference = authorReference;

		if (xml['ZNT']['ZNT.3'][i]['ZNT.3.1'].toString().trim() != "")
		{
			//logger.debug("ZNT IF run date" +  xml['ZNT']['ZNT.3'][i]['ZNT.3.1']);
			valueAnnotation.time = GetISODate(xml['ZNT']['ZNT.3'][i]['ZNT.3.1']);
		}
		else
		{
			valueAnnotation.time = "";
		}

		valueAnnotation.text = xml['NTE']['NTE.3'][i]['NTE.3.1'];

		//
		//build extension array
		//
		var extension = [
			    {"url":"https://api.excelleris.com/1.0/eorder/StructureDefinition/NoteFrequency", "valueString":""},
			    {"url":"https://api.excelleris.com/1.0/eorder/StructureDefinition/NoteType", "valueCode":""}
		];

		//
		//populate extension
		//
		for each (item in extension)
		{
			if (item.url.toString().toUpperCase().indexOf('NOTEFREQUENCY') > -1)
			{
				item.valueString = xml['ZNT']['ZNT.2'][i]['ZNT.2.1'];
				//logger.debug("NoteFreq found. ZNT 2 is: " + xml['ZNT']['ZNT.2'][i]['ZNT.2.1']);
				//logger.debug("item.valueString: " + item.valueString);
			}			
			else if (item.url.toString().toUpperCase().indexOf('NOTETYPE') > -1)
			{
				item.valueCode = xml['NTE']['NTE.4'][i]['NTE.4.1'];
				//logger.debug("NOTETYPE found. NTE 4 is: " + xml['NTE']['NTE.4'][i]['NTE.4.1']);
				//logger.debug("item.valueString: " + item.valueCode);
			}
			valueAnnotation.extension = extension;
		}
		//populate extensions array
		extensions[i]['url'] = "https://api.excelleris.com/1.0/eorder/StructureDefinition/Note";
		extensions[i]['valueAnnotation'] = valueAnnotation;

	}//END OF test loop

     if (GetTimeFromMSH(xml['MSH']['MSH.10']['MSH.10.1'])!= ""){
		timestamp = {"url":"https://api.excelleris.com/1.0/eorder/StructureDefinition/timestamp",
		             "valueString" : ""};
		timestamp.valueString = GetTimeFromMSH(xml['MSH']['MSH.10']['MSH.10.1']);			
		extensions.push(timestamp);
     }
	//put extensions array into JSON FHIR template
	logger.debug(extensions.length);
	logger.debug(JSON.stringify(extensions));
	if (extensions.length >= 1){
	  tmp['extension'] = extensions; //extension; 
	}



	} //end of create patient notes section

	//
	//return a FHIR Patient Resource 
	//

	//don't stringify if order bundle
	//don't clean up if order bundle
	if (isBundle == false)
	{
		//clean up  - OLIS spec doesn't use.	
		delete tmp['maritalStatus'];
		delete tmp['photo'];
		delete tmp['communication'];
		delete tmp['contact'];
		delete tmp['managingOrganization'];
		//delete tmp['identifier'][1];
		delete tmp['id'];
		delete tmp['text'];
		delete tmp['multipleBirthBoolean'];

		return JSON.stringify(tmp);
	}

	//FHIR Bundle - only clean up template if we are on last order
	if (isLastOrder == true)
	{
		//clean up  - OLIS spec doesn't use.	
		delete tmp['maritalStatus'];
		delete tmp['photo'];
		delete tmp['communication'];
		delete tmp['contact'];
		delete tmp['managingOrganization'];
		//delete tmp['identifier'][1];
		delete tmp['id'];
		delete tmp['text'];
		delete tmp['multipleBirthBoolean'];
	}

	logger.debug("Patient Resource:" + JSON.stringify(tmp));
	return tmp;
}

/**
	Creates a FHIR patient resource for FHIR Bundle.

	@return {json} FHIR Patient Resource
*/
// Changes XML to JSON
//xml = xml version of the hl7 response from IntRLab
//tmp = outbound message template (json)
function CreatePatientResourceForBundle(inBundle, inHL7) 
{
	logger.info("In code template.  CreatePatientResourceForBundle xml: ");
	//logger.info("In code template.  CreatePatientResource tmp: " + JSON.stringify(tmp));
	//(inHL7['PID']['PID.5']['PID.5.3'] != undefined) ? true : false

	//create resource
	var PatientRes 	= new Object();

	//create resourceType
	PatientRes.resourceType = "Patient";

	//create extensions for patient notes
	/*
	//if NTE/ZNT under PID...
	PatientRes.extension = new Array();

	//create valueAnnotation
	var valueAnnotation = new Object();
	valueAnnotation.extension = new Array();
	valueAnnotation.extension[0] = {"url":"https://api.excelleris.com/1.0/eorder/StructureDefinition/NoteFrequency", "valueString":""};
	valueAnnotation.extension[1] = {"url":"https://api.excelleris.com/1.0/eorder/StructureDefinition/timestamp", "valueString":""};
	valueAnnotation.extension[2] = {"url":"https://api.excelleris.com/1.0/eorder/StructureDefinition/NoteType", "valueString":""};
	valueAnnotation.authorReference = {"reference":"person/id", "display":"User that made the note"};
	valueAnnotation.time = "";
	valueAnnotation.text = "";

	PatientRes.extension[0] = {"url":"https://api.excelleris.com/1.0/eorder/StructureDefinition/PatientNote", "valueAnnotation":valueAnnotation};
	*/	

	//create identifier array
	if (inHL7['PID']['PID.3']['PID.3.5'] == "JHN")
	{

		//create identifier array
		PatientRes.identifier = new Array();
		var objCode 	= new Array();
		objCode[0] 	= {"system":"hcn", "code": inHL7['PID']['PID.3']['PID.3.1']};
		objCode.code 	= "";
		var coding 	=  objCode ;
		var arrCoding 	= new Object();
		arrCoding.coding 	= coding ;
		var objIdentifier 	= new Object();
		objIdentifier.use 	= "official";
		objIdentifier.type 	= {"coding":coding};
		PatientRes.identifier[0] = objIdentifier;
	}

	//create name array
	if (inHL7['PID']['PID.5']['PID.5.1'].toString().trim() != "")
	{

		//entryPatientRes['identifier'][0]['type']['coding'][0]['code']   = inHL7['PID']['PID.3']['PID.3.1'];
		//entryPatientRes['identifier'][0]['type']['coding'][0]['system'] = "hcn";
		PatientRes.name = new Array();

		var nameObj1 = new Object();
		nameObj1.use = "usual";
		nameObj1.family = inHL7['PID']['PID.5']['PID.5.1'];

		var arrGiven = new Array();
		arrGiven[0] = inHL7['PID']['PID.5']['PID.5.2']; //first name
		arrGiven[1] = inHL7['PID']['PID.5']['PID.5.3']; //middle name

		nameObj1.given = arrGiven;
		PatientRes.name[0] = nameObj1;

		if (inHL7['ZPD'])
		{
			var nameObj2 = new Object();
			nameObj2.use = "nickname";
			nameObj2.family = inHL7['ZPD']['ZPD.7']['ZPD.7.1'];

			PatientRes.name[1] = nameObj2;
		}
	}


	if (inHL7['PID']['PID.13'].toString().trim() != "")
	{
		//create telecom
		var arrTelcom = new Array();
		var phoneRank = 1;

		for each(telecomItem in inHL7['PID']['PID.13'])
		{
			if (telecomItem.toString().trim() != "")
			{
				logger.debug("Found PID-13. Create telecom fhir object: " + JSON.stringify(inHL7['PID']['PID.13']));
				var objTelecom = new Object();
				objTelecom.system 	= GetFHIRTelecomSystem(telecomItem['PID.13.3']);
				//add dashes
				var phone1 	   	= telecomItem['PID.13.7'].substring(0, 3);
				var phone2  	   	= telecomItem['PID.13.7'].substring(3, 7);
				var phoneFinal    	= phone1 + "-" + phone2;
				objTelecom.value 	= telecomItem['PID.13.6'] + "-" + phoneFinal;
				objTelecom.use 	= GetFHIRTelecomUse(telecomItem['PID.13.2']);

				//rank #1 for primary PRN/PHONE and rank #2 for secondary
				if (telecomItem['PID.13.2'] == "PRN" &&
				    telecomItem['PID.13.3'] == "PH")
				{
					//add rank. first one should be primary, second one secondary
					objTelecom.rank 	= phoneRank;
					phoneRank++;
				}

				arrTelcom[arrTelcom.length] = objTelecom;
			}//end of telecom


		}//end of telecom loop

	PatientRes.telecom = arrTelcom;
	}





	//create gender
	if (inHL7['PID']['PID.8']['PID.8.1'].toString().trim() != "")
	{

		PatientRes.gender = GetFHIRGender(inHL7['PID']['PID.8']['PID.8.1']);
	}

	//create b-day
	if (inHL7['PID']['PID.7']['PID.7.1'].toString().trim() != "")
	{

		PatientRes.birthDate = inHL7['PID']['PID.7']['PID.7.1'];
	}


	//create address
	if (inHL7['PID']['PID.11']['PID.11.1'].toString().trim() != "")
	{
		blnPatientData 	= true;
		PatientRes.address 	= new Array();
		var objAddress 	= new Object();
		objAddress.use	     = "home"

		if (inHL7['PID']['PID.11']['PID.11.7'] == "H")
		{
			objAddress.use   = "home"; //PId-11.7
		}
		objAddress.line       = inHL7['PID']['PID.11']['PID.11.1'].split("\\.br\\");; //pid-11.2=line2?
		objAddress.city       = inHL7['PID']['PID.11']['PID.11.3'];
		objAddress.state 	  = inHL7['PID']['PID.11']['PID.11.4'];
		objAddress.postalCode = inHL7['PID']['PID.11']['PID.11.5'];
		objAddress.country 	  = inHL7['PID']['PID.11']['PID.11.6'];
		PatientRes.address[0] = objAddress;
	}

	//any data?
	if (!isObjectEmpty(PatientRes))
	{
		//add fullUrl:
		var PatientResource = "{ \"resource\" " + ":" + JSON.stringify(PatientRes) + "}";
		//logger.debug("New Patient Resourcee: " +  JSON.stringify(PatientRes));
		return JSON.parse(PatientResource);
	}
	else
	{
		return undefined;
	}
}

/**
	Creates a FHIR practitioner resource for FHIR Bundle.

	@param {json} inBundle - outbound message template (json)
	@param {Any} inHL7 - HL7 response from IntRLab
	@return {json,boolean} return Location resource or false no data message. 

*/
function CreatePractitionerResource(inBundle, inHL7)
{
	//logger.debug("IN  CreatePractitionerResource: ");

	//create resource
	var PractitionerRes 	  = new Object();
     logger.debug(inHL7);
	if (inHL7['OBR']['OBR.16'][0]['OBR.16.1'].toString().trim() != "")
	{
		//create resourceType
		PractitionerRes.resourceType = "Practitioner";

		//create identifier array
		PractitionerRes.identifier = [{"use": "", "value": ""}];
		PractitionerRes.identifier[0].use = "official";
		PractitionerRes.identifier[0].value = inHL7['OBR']['OBR.16'][0]['OBR.16.1']; //ID

		//create name object
		PractitionerRes.name = new Object();
		PractitionerRes.name.family = new Array(inHL7['OBR']['OBR.16'][0]['OBR.16.2']);
		PractitionerRes.name.given  = new Array(inHL7['OBR']['OBR.16'][0]['OBR.16.3']);
		PractitionerRes.name.prefix = new Array(inHL7['OBR']['OBR.16'][0]['OBR.16.6']);

		//create practitioner role
		PractitionerRes.practitionerRole = new Array();
		var role = new Object();
		role.coding = new Array();
		var roleCoding = new Object();
		roleCoding.system = "role";
		roleCoding.code = "doctor";
		role.coding[0] = roleCoding ;
		PractitionerRes.practitionerRole[0] = "{ \"role\" " + ":" +  JSON.stringify(role) + "}";
		PractitionerRes.practitionerRole[0] = JSON.parse(PractitionerRes.practitionerRole[0]);


		//for each(entryPractitionerRes in PractitionerRes)
		//{
			//entryPractitionerRes['practitionerRole'][0]['role']['coding'][0]['code'] = "doctor";
			//entryPractitionerRes['identifier'][0]['value'] = inHL7['PV1']['PV1.7']['PV1.7.1']; //ID
			//entryPractitionerRes['name']['family'][0] 	  = inHL7['PV1']['PV1.7']['PV1.7.2']; //last
			//entryPractitionerRes['name']['given'][0] 	  = inHL7['PV1']['PV1.7']['PV1.7.3']; //first
			//entryPractitionerRes['name']['prefix'][0] 	  = inHL7['PV1']['PV1.7']['PV1.7.6']; //prefix
		//}
	}


	//TODO: ask dan about where to get address from.

	//create telecom array
	//PractitionerRes.telecom = new Array();
	//PractitionerRes.telecom[0] = {"system":"phone","value":"", "use":"work"};
	//PractitionerRes.telecom[1] = {"system":"phone","value":""};

	//create address array
	/*PractitionerRes.address = new Array(4);
	PractitionerRes.address[0] = [{"line":""}]
	PractitionerRes.address[1] = {"city":""};
	PractitionerRes.address[2] = {"state":""};
	PractitionerRes.address[3] = {"postalcode":""};
	PractitionerRes.address[4] = {"country":""};
	PractitionerRes.address = new Array();
	var addressLine = new Object();
	addressLine.line = new Array(""); //address line 1 & 2
	addressLine.city = "";
	addressLine.state = "";
	addressLine.postalCode = "";
	addressLine.country = "";
	PractitionerRes.address[0] = addressLine ;
	*/

	if (!isObjectEmpty(PractitionerRes))
	{
		var PractitionerResource = "{ \"resource\" " + ":" + JSON.stringify(PractitionerRes) + "}";
		//logger.debug("Encounter NEW: " + JSON.stringify(JSON.parse(PractitionerResource)));
		return JSON.parse(PractitionerResource);

	}else
	{
		return undefined;
	}
}

/**
	Creates a response containing FHIR resource with IntRLab accession number.

		@param {Any} inAccessionNumber - 
		@return {String} return description
*/
function CreateFHIRResponse(inTemplate, inAccessionNumber) {
	// TODO: Enter code here
	try
	{
		var inJsonObj    = JSON.parse(inTemplate);

		if (inJsonObj.identifier !=  undefined)
		{
			inJsonObj.identifier[0].value = inAccessionNumber;
		}
		//logger.debug("value: " + inJsonObj.identifier[0].value);
		//logger.debug("In CreateFHIRResponse Code Template JSON: " + JSON.stringify(inJsonObj));
		//logger.debug("In CreateFHIRResponse Code Template inAccessionNumber: " + inAccessionNumber);

		//convert template to JSON
		return inJsonObj; //JSON.stringify(inJsonObj);

	}
	catch(err)
	{
		logger.error(err);
		return "500";	
	}

}

/**
	LifeLabs function to create HL7 from FHIR.

	@param {JSON} inFHIR - FHIR object (JSON)
	@param {XML} inHL7 - HL7 Object (XML)
	@param {String} messageId - pass context.getMessageId()
	@return {String} HL7 format
*/
//USES:  "HL7 Translations (LifeLabs)" Code Template Library
function CreateHL7fromFHIR(inFHIR, inHL7, messageId)
{
	//logger.debug("IN CreateHL7fromFHIR CODE TEMPLATE inHL7: " + inHL7); //no length

	//var arrOrderDetails   = HasMultipleOrdersInFHIRBundle(inFHIR).split("|");
	//var blnHasMultiTests  = arrOrderDetails[0];
	//var cntTests          = arrOrderDetails[1];
	//logger.debug("Multi Tests?: " + blnHasMultiTests);
	//logger.debug("How Many Tests?: " + cntTests);

	var curORMXML = new XML("<HL7Message></HL7Message>");

	//resources used in this bundle
	var PatientRes 		= new Array();
	var PractitionerRes 	= new Array();
	var EncounterRes		= new Array();
	var LocationRes		= new Array();
	var DiagnosticOrderRes	= new Array();

	//diagnstic order extensions in bundle
	var extServiceDate					= "";
	var extPayerType					= "";
	var extCollectionDate				= "";
	var extSubjectDiagnosis				= "";
	var extStatTransportMode 			= "";
	var extStatETA						= "";
	var extStatSpun					= "";
	var extStatInstructions  			= "";
	var extSupplementalLabels 			= "";
	var extChartNumberClientInfo 			= "";
	var extResultsContact				= "";
	var extResultsContactPhone 			= "";
	var extResultsContactPhoneAfterHrs 	= "";
	var extResultsContactPhoneInstrutions 	= "";
	var extResultsContactFax				= "";
	var extResultsContactFaxAttn			= "";
	var extMyEHealth					= "";
	var extContactPointSystem			= "";
	var extContactPointValue				= "";
	var extContactPointExtension			= "";
	var extCopyToRecipientUIDs			= new Array();
	var extCopyToPatientUID				= "";
	var extSubjectDiagnosis				= "";
	var extPatientNotes					= "";

	var patientNickname					= "";

	var cntExtensions					= 0;
	var cntCopyTo						= 0;
	var cntPreReportables;
	var cntPatientNotes					= 0;


try{

		//
		// get resources - GetResourceFromBundle code at the end of this page.
		//
		PatientRes      	= GetResourceFromBundle(inFHIR, "PATIENT");
		PractitionerRes 	= GetResourceFromBundle(inFHIR, "PRACTITIONER");
		DiagnosticOrderRes  = GetResourceFromBundle(inFHIR, "DIAGNOSTICORDER");
		EncounterRes		= GetResourceFromBundle(inFHIR, "ENCOUNTER");
		LocationRes		= GetResourceFromBundle(inFHIR, "LOCATION");



		//
		//Loop through extensions now. Numerous segments will need.
		//
		for each( extensionItem in DiagnosticOrderRes[0].extension)
		{
			//logger.debug("extensionItem: " + JSON.stringify(extensionItem));
			if (extensionItem.url == "https://api.excelleris.com/1.0/eorder/diagnosticorder-serviceDateTime")
			{
				extServiceDate = extensionItem.valueString;
			}
			if (extensionItem.url == "https://api.excelleris.com/1.0/eorder/diagnosticorder-payertype")
			{
				extPayerType 	= extensionItem.valueString;
			}
			if (extensionItem.url == "https://api.excelleris.com/1.0/eorder/diagnosticorder-collectionDateTime")
			{
				extCollectionDate = extensionItem.valueString;
			}
			if (extensionItem.url == "https://api.excelleris.com/1.0/eorder/diagnosticorder-resultsContact")
			{
				extResultsContact = extensionItem.valueString;
			}
			if (extensionItem.url == "https://api.excelleris.com/1.0/eorder/diagnosticorder-statTransportMode")
			{
				extStatTransportMode = extensionItem.valueString;
			}
			if (extensionItem.url == "https://api.excelleris.com/1.0/eorder/diagnosticorder-statETA")
			{
				extStatETA = extensionItem.valueString;
			}
			if (extensionItem.url == "https://api.excelleris.com/1.0/eorder/diagnosticorder-statSpun")
			{
				extStatSpun = extensionItem.valueString;
			}
			if (extensionItem.url == "https://api.excelleris.com/1.0/eorder/diagnosticorder-statInstructions")
			{
				extStatInstructions = extensionItem.valueString;
			}
			if (extensionItem.url == "https://api.excelleris.com/1.0/eorder/diagnosticorder-supplementalLabels")
			{
				extSupplementalLabels = extensionItem.valueString;
			}
			if (extensionItem.url == "https://api.excelleris.com/1.0/eorder/eorder-contactpoint")
			{
				if (extensionItem.valueContactPoint)
				{
					extContactPointSystem 	= extensionItem.valueContactPoint.system;
					extContactPointValue 	= extensionItem.valueContactPoint.value;
					extContactPointExtension = extensionItem.valueContactPoint.extension;
				}
			}
			if (extensionItem.url == "https://api.excelleris.com/1.0/eorder/diagnosticorder-myEHealth")
			{
				extMyEHealth = extensionItem.valueBoolean;
			}
			if (extensionItem.url == "https://api.excelleris.com/1.0/eorder/diagnosticorder-copytopatient")
			{
				//logger.debug("Found copy to PATIENT: " + extensionItem.valueReference.reference);
				extCopyToPatientUID = extensionItem.valueReference.reference;
				cntCopyTo++;
			}
			if (extensionItem.url == "https://api.excelleris.com/1.0/eorder/diagnosticorder-copytorecipient")
			{
				//logger.debug("Found copy to DOCTORS:" + extensionItem.valueReference.reference);
				extCopyToRecipientUIDs[cntExtensions] = extensionItem.valueReference.reference;
				cntExtensions++;
				cntCopyTo++;
			}
			if (extensionItem.url == "https://api.excelleris.com/1.0/eorder/diagnosticorder-chartNumberClientInfo")
			{
				extChartNumberClientInfo =  extensionItem.valueString;
			}
			if (extensionItem.url == "https://api.excelleris.com/1.0/eorder/diagnosticorder-subjectdiagnosis")
			{
				extSubjectDiagnosis  = extensionItem.valueString;
			}

		}

		//
		//MSH - required
		//
		{
		//sending application
		//inHL7['MSH']['MSH.3']['MSH.3.1'] = "";
		inHL7['MSH']['MSH.3']['MSH.3.2'] = "WEB_GUI";
		inHL7['MSH']['MSH.3']['MSH.3.3'] = "X500"; //X500 or ISO
		//sending facility
		inHL7['MSH']['MSH.4']['MSH.4.1'] = "LIFELABS BC";
		//receiving
		//inHL7['MSH']['MSH.5']['MSH.5.1'] = "";
		inHL7['MSH']['MSH.5']['MSH.5.2'] = "OLIS"; //"INTRLAB";
		inHL7['MSH']['MSH.5']['MSH.5.3'] = "X500";
		//logger.debug("MSH7");
		inHL7['MSH']['MSH.7']['MSH.7.1'] = GetHL7Date(new Date().toISOString());
		inHL7['MSH']['MSH.9']['MSH.9.1'] = "ORM";
		inHL7['MSH']['MSH.9']['MSH.9.2'] = "O01";
		inHL7['MSH']['MSH.9']['MSH.9.3'] = "ORM_O01";
		//MSH-10 - from GET Timestamp if UPDATE
		//Leave blank for new 			
		inHL7['MSH']['MSH.10']['MSH.10.1'] = DateUtil.getCurrentDate('yyyyMMdd') + org.apache.commons.lang3.StringUtils.leftPad(messageId,6,'0'); //"63936"; //"63936.2-63937.320.332903";
		//P=PROD, C=Conformance, S=Self-Test, T=Training
		inHL7['MSH']['MSH.11']['MSH.11.1'] = "S";
		inHL7['MSH']['MSH.12']['MSH.12.1'] = "2.3.1";
		inHL7['MSH']['MSH.18']['MSH.18.1'] = "8859/1";
		//grab current MSH
		curORMXML.appendChild(new XML(inHL7['MSH']));
		//logger.debug("MSH Added");
		}

		//
		//PID - required
		//
		{

		if (PatientRes.length == 1)
		{
			for each(entryPatientRes in PatientRes)
			{
				if (entryPatientRes['identifier'][0]['type']['coding'][0]['system'].toString().toUpperCase() == "HCN")
				{
					inHL7['PID']['PID.3']['PID.3.1'] 	= entryPatientRes['identifier'][0]['type']['coding'][0]['code'];
					inHL7['PID']['PID.3']['PID.3.5'] 	= GetSystem(entryPatientRes['identifier'][0]['type']['coding'][0]['system']);
				}
				inHL7['PID']['PID.3']['PID.3.9']['PID.3.9.1'] = "BC";
				inHL7['PID']['PID.3']['PID.3.9']['PID.3.9.2'] = "BRITISH COLUMBIA";
				inHL7['PID']['PID.3']['PID.3.9']['PID.3.9.3'] = "HL70347";

				//patient name array
				for each (itemName in entryPatientRes.name)
				{
					if (itemName.use == "usual")
					{
						inHL7['PID']['PID.5']['PID.5.1'] 	= itemName.family; 
						inHL7['PID']['PID.5']['PID.5.2'] 	= itemName.given[0];
						inHL7['PID']['PID.5']['PID.5.3'] 	= (itemName.given[1] != undefined) ? itemName.given[1] : "";  
					}
					if (itemName.use == "nickname")
					{
						patientNickname = itemName.family;
					}
				}

				inHL7['PID']['PID.5']['PID.5.7']    = "U";
				inHL7['PID']['PID.7']['PID.7.1'] 	= GetHL7BirthDate(entryPatientRes['birthDate']);
				inHL7['PID']['PID.8']['PID.8.1'] 	= GetGender(entryPatientRes['gender']);

				//patient address array
				for each (itemAddress in entryPatientRes.address)
				{
					//copy PID-11 and change type to "M"
					if (extCopyToPatientUID)
					{
						logger.debug("---//copytopatient, add 2nd address of type M: " + JSON.stringify(inHL7['PID']['PID.11']));
						//loop and create repeating address
						for(x = 0; x < 2; x++)
						{
							inHL7['PID']['PID.11'][x]['PID.11.1'] = "repeating";
							logger.debug("---//copytopatient creatign array: " +  JSON.stringify(inHL7['PID']['PID.11'][x]['PID.11.1']));

							if (x == 0)
							{
								inHL7['PID']['PID.11'][x]['PID.11.1'] = itemAddress.line[0] + ((itemAddress.line[1]  != undefined) ? "\\.br\\" + itemAddress.line[1] : "");
								inHL7['PID']['PID.11'][x]['PID.11.3'] = itemAddress.city;
								inHL7['PID']['PID.11'][x]['PID.11.4'] = itemAddress.state;
								inHL7['PID']['PID.11'][x]['PID.11.5'] = itemAddress.postalCode;
								inHL7['PID']['PID.11'][x]['PID.11.6'] = itemAddress.country;
								inHL7['PID']['PID.11'][x]['PID.11.7']  = GetAddressUse(itemAddress.use);

							}
							else if (x == 1)
							{
								inHL7['PID']['PID.11'][x]['PID.11.1'] = itemAddress.line[0]+ ((itemAddress.line[1]  != undefined) ? "\\.br\\" + itemAddress.line[1] : "");
								inHL7['PID']['PID.11'][x]['PID.11.3'] = itemAddress.city;
								inHL7['PID']['PID.11'][x]['PID.11.4'] = itemAddress.state;
								inHL7['PID']['PID.11'][x]['PID.11.5'] = itemAddress.postalCode;
								inHL7['PID']['PID.11'][x]['PID.11.6'] = itemAddress.country;
								inHL7['PID']['PID.11'][x]['PID.11.7']  = "M";

							}

						}
					}
					else
					{
						logger.debug("no patient copy to address: " +  JSON.stringify(inHL7['PID']['PID.11'][0]));
						if (itemAddress.use == "home")
						{
							inHL7['PID']['PID.11'][0]['PID.11.1'] = itemAddress.line[0] + ((itemAddress.line[1]  != undefined) ? "\\.br\\" + itemAddress.line[1] : "");

							inHL7['PID']['PID.11'][0]['PID.11.3'] = itemAddress.city;
							inHL7['PID']['PID.11'][0]['PID.11.4'] = itemAddress.state;
							inHL7['PID']['PID.11'][0]['PID.11.5'] = itemAddress.postalCode;
							inHL7['PID']['PID.11'][0]['PID.11.6'] = itemAddress.country;
						}
						if (itemAddress.use){
							inHL7['PID']['PID.11'][0]['PID.11.7']  = GetAddressUse(itemAddress.use);
						} else
						{
							inHL7['PID']['PID.11'][0]['PID.11.7']  = GetAddressUse("home"); 
						}
						//delete repeating
						delete  inHL7['PID']['PID.11'][1];
					}
				}



				//patient telecom array
				for each (telecom in entryPatientRes.telecom)
				{
					//logger.debug("telecom : " +  JSON.stringify(telecom));
					//repeat if secondary exists - if this fails take out.

					if (telecom.use == "home") //home
					{
						inHL7['PID']['PID.13']['PID.13.2'] = GetTelecomUse(telecom.use);
						inHL7['PID']['PID.13']['PID.13.3'] = GetTelecomSystem(telecom.system);
						inHL7['PID']['PID.13']['PID.13.6'] = GetTelecomNumber(telecom.value, true);
						inHL7['PID']['PID.13']['PID.13.7'] = GetTelecomNumber(telecom.value, false);	
					}
					else
					{
						logger.debug("telecom secondary number exists: ");
						inHL7['PID']['PID.13']['PID.13.2'] = "ORN"; //secondary
						inHL7['PID']['PID.13']['PID.13.3'] = GetTelecomSystem(telecom.system);
						inHL7['PID']['PID.13']['PID.13.6'] = GetTelecomNumber(telecom.value, true);
						inHL7['PID']['PID.13']['PID.13.7'] = GetTelecomNumber(telecom.value, false);	
					}

				}


				}

			//standing order number
			//inHL7['PID']['PID.3']['PID.3.11'] 	= "VC";
		}

		//grab current PID
		curORMXML.appendChild(new XML(inHL7['PID']));
		//logger.debug("PID Added");

		} // end of PID section

		//
		//ZPD - PID extension segment
		//
		{
		//ZPD|||Y|
		//1 - Patient Consent Indicator (no longer supported)
		//2 - Patient Identification Verified Flag. Must be "Y" or empty.
		//3 - Patient Consent block-all indicator
		inHL7['ZPD'] 			= "|";
		inHL7['ZPD']['ZPD.5'] 	= extSubjectDiagnosis;
		inHL7['ZPD']['ZPD.7']	= patientNickname;
		//grab current PID
		curORMXML.appendChild(new XML(inHL7['ZPD']));
		//logger.debug("ZPD added");

		}//end of ZPD section


		//NTE-ZNT Segment Pair
		{

		//
		//Loop through extensions .
		//
		var cntPatientLoop = 0;
		for each( extensionItem in PatientRes[0].extension)
		{	
			if (extensionItem.url == "https://api.excelleris.com/1.0/eorder/StructureDefinition/Note")
			{
				if (extensionItem.valueAnnotation)
				{
					logger.debug("PatientNotes counter:" + cntPatientNotes);
					logger.debug("Patient Loop counter:" + cntPatientLoop);
					//NTE
					//The Global Comments will be passed as PID level NTE - Patient Notes
					//4.1 Identifier
					//4.2 Text
					//4.3 Name of Coding System
					inHL7['NTE'][cntPatientNotes] = new XML("<NTE></NTE>");
					inHL7['NTE'][cntPatientNotes]['NTE.1']['NTE.1.1'] = cntPatientLoop + 1;
					inHL7['NTE'][cntPatientNotes]['NTE.2']['NTE.2.1'] = "P";
					inHL7['NTE'][cntPatientNotes]['NTE.3']['NTE.3.1'] = extensionItem.valueAnnotation.text;
					inHL7['NTE'][cntPatientNotes]['NTE.4']['NTE.4.1'] = "PATINST";
					inHL7['NTE'][cntPatientNotes]['NTE.4']['NTE.4.2'] = "PATIENT INSTRUCTION";
					inHL7['NTE'][cntPatientNotes]['NTE.4']['NTE.4.3'] = "LIFELABS";

					//grab current segment

					//ZNT - NTE note extension
					inHL7['ZNT'][cntPatientNotes] = new XML("<ZNT></ZNT>");
					inHL7['ZNT'][cntPatientNotes]['ZNT.1']['ZNT.1.1'] = "";
					inHL7['ZNT'][cntPatientNotes]['ZNT.1']['ZNT.1.2'] = "LIFELABS";
					inHL7['ZNT'][cntPatientNotes]['ZNT.1']['ZNT.1.3'] = "X500";

					//loop extensions
					for each (ext in extensionItem.valueAnnotation.extension)
					{
						if (ext.url == "https://api.excelleris.com/1.0/eorder/StructureDefinition/NoteFrequency")
						{
							//logger.debug("Found notefrequency extension. Put into ZNT2.1");
							inHL7['ZNT'][cntPatientNotes]['ZNT.2']['ZNT.2.1'] = ext.valueString;
						}
						if (ext.url == "https://api.excelleris.com/1.0/eorder/StructureDefinition/timestamp")
						{
							//logger.debug("Found timestamp extension. Put into MSH-10");
							//curORMXML['MSH']['MSH.10']['MSH.10.1'] = ext.valueString;	
						}
						if (ext.url == "https://api.excelleris.com/1.0/eorder/StructureDefinition/NoteType")
						{
							//logger.debug("Found timestamp note type. Put into NTE4.1");
							inHL7['NTE'][cntPatientNotes]['NTE.4']['NTE.4.1'] = ext.valueCode;
						}

					}
					logger.debug("-----------extensionItem.valueAnnotation.time...." +  extensionItem.valueAnnotation.time.replace(/-/g,""));//23-Mar-2016
					inHL7['ZNT'][cntPatientNotes]['ZNT.3']['ZNT.3.1'] =  GetHL7DateFromISO(extensionItem.valueAnnotation.time, "yyyyMMdd"); 

					//grab current segment
					curORMXML.appendChild(new XML(inHL7['NTE'][cntPatientNotes]));

					//grab current segment
					curORMXML.appendChild(new XML(inHL7['ZNT'][cntPatientNotes]));
				}
				cntPatientLoop++
				cntPatientNotes++;
			}
		}

		logger.debug("Patient notes size: " + cntPatientNotes + " " + cntPatientLoop);

          //GLOBCOMM         
		for each(note in DiagnosticOrderRes[0].note)
		{
				logger.debug("GLOBCOM:" + note.text);
				logger.debug("PatientNotes counter:" + cntPatientNotes);
				logger.debug("Patient Loop counter:" + cntPatientLoop);
			     //NTE
				//The Global Comments will be passed as PID level NTE - Patient Notes

				inHL7['NTE'][cntPatientNotes] = new XML("<NTE></NTE>");
				inHL7['NTE'][cntPatientNotes]['NTE.1']['NTE.1.1'] = cntPatientLoop+1;
				inHL7['NTE'][cntPatientNotes]['NTE.2']['NTE.2.1'] = "L";
				//if a canned comment was used then pass that in NTE.3, otherwise just send the free text
				if (note.extension.getByUrl("https://api.excelleris.com/1.0/eorder/StructureDefinition/CannedComment")){
				    inHL7['NTE'][cntPatientNotes]['NTE.3']['NTE.3.1'] = note.extension.getByUrl("https://api.excelleris.com/1.0/eorder/StructureDefinition/CannedComment").valueCode.toUpperCase();
				} else 
				{
				    inHL7['NTE'][cntPatientNotes]['NTE.3']['NTE.3.1'] = note.text.replace(/\r\n/g, '\\.br\\').toUpperCase();
				}
				inHL7['NTE'][cntPatientNotes]['NTE.4']['NTE.4.1'] = note.extension.getByUrl("https://api.excelleris.com/1.0/eorder/StructureDefinition/NoteType").valueCode;
				inHL7['NTE'][cntPatientNotes]['NTE.4']['NTE.4.2'] = "ORDER GLOBAL COMMENT";
				inHL7['NTE'][cntPatientNotes]['NTE.4']['NTE.4.3'] = "LIFELABS";

				//grab current segment

				//ZNT - NTE note extension
				inHL7['ZNT'][cntPatientNotes] = new XML("<ZNT></ZNT>");
				//inHL7['ZNT'][cntPatientNotes]['ZNT.1']['ZNT.1.1'] = "";
				//inHL7['ZNT'][cntPatientNotes]['ZNT.1']['ZNT.1.2'] = "LIFELABS";
				//inHL7['ZNT'][cntPatientNotes]['ZNT.1']['ZNT.1.3'] = "X500";

				//grab current segment
				curORMXML.appendChild(new XML(inHL7['NTE'][cntPatientNotes]));

				//grab current segment
				curORMXML.appendChild(new XML(inHL7['ZNT'][cntPatientNotes]));
				cntPatientLoop++
				cntPatientNotes++;

			}

		}

		//PV1 - Visit Information
		{
			logger.debug("LocationRes");
			if (LocationRes.length == 1)
			{
				//Original Accession# will be in PV1.19, Note original Prefix will be deduced from the first 3 digits of the accession number
				inHL7['PV1']['PV1.2']['PV1.2.1'] = GetPatientClass("Z");

				for each(entryLocationRes in LocationRes)
				{
					inHL7['PV1']['PV1.3']['PV1.3.1'] = entryLocationRes['identifier'][0]['value']; 	
				}
			}

			logger.debug("PractitionerRes");

			if (PractitionerRes.length >= 1)
			{
				for each(entryPractitionerRes in PractitionerRes)
				{
					//logger.debug("entryPractitionerRes: " + JSON.stringify(entryPractitionerRes));
					logger.debug("checking role");
					if (entryPractitionerRes['practitionerRole']){
					if (entryPractitionerRes['practitionerRole'][0]['role']['coding'][0]['code'] == "doctor")
					{
						//attending doctor
						inHL7['PV1']['PV1.7']['PV1.7.1'] = entryPractitionerRes['identifier'][0]['value']; //ID
						inHL7['PV1']['PV1.7']['PV1.7.2'] = entryPractitionerRes['name']['family'][0]; //last
						inHL7['PV1']['PV1.7']['PV1.7.3'] = entryPractitionerRes['name']['given'][0]; //first
						inHL7['PV1']['PV1.7']['PV1.7.6'] = validate(entryPractitionerRes['name']['prefix'][0],""); //prefix
					}


					inHL7['PV1']['PV1.7']['PV1.7.13'] = "MDL";
					inHL7['PV1']['PV1.7']['PV1.7.22']['PV1.7.22.1'] = "BC";
					inHL7['PV1']['PV1.7']['PV1.7.22']['PV1.7.22.2'] = "BRITISH COLUMBIA";
					inHL7['PV1']['PV1.7']['PV1.7.22']['PV1.7.22.3'] = "HL70347";
					}
					//IntRLab accession-level collection date - PV1.44 - return only date no time
					logger.debug("mapping collection date");
					inHL7['PV1']['PV1.44']['PV1.44.1'] = GetHL7DateFromISO(extCollectionDate, "yyyyMMddHHmmssZZZZ");

					//PV1-19 will hold original accession number
					//inHL7['PV1']['PV1.19']['PV1.19.1'] = "150019001"; //
					//chart number
					if (extChartNumberClientInfo)
					{
						inHL7['PV1']['PV1.50'] = extChartNumberClientInfo;
					}
				}

			}
			//grab current segment
			curORMXML.appendChild(new XML(inHL7['PV1']));

			logger.debug("PV1 added");
		} //end of PV1


		//ORC-OBR-OBX-NTE-ZNT
		{
		if (DiagnosticOrderRes.length == 1)
		{
			for each(entryDiagnosticOrderRes in DiagnosticOrderRes)
			{
				for (var x = 0; x < entryDiagnosticOrderRes.item.length; x++)
				{

					inHL7['ORC']['ORC.1']['ORC.1.1'] = getAction();
					inHL7['ORC']['ORC.4']['ORC.4.1'] = "90129531111"; 
					inHL7['ORC']['ORC.4']['ORC.4.3'] = "2.16.840.1.113883.3.59.1:9999";
					inHL7['ORC']['ORC.4']['ORC.4.4'] = "ISO";
					inHL7['ORC']['ORC.9']['ORC.9.1'] = GetHL7Date(new Date().toISOString());
					//grab current segment
					curORMXML.appendChild(new XML(inHL7['ORC']));
					logger.debug("ORC added");
					//LifeLab's OID = 2.16.840.1.113883.3.1863

					inHL7['OBR']['OBR.1']['OBR.1.1'] = x + 1;
		     		inHL7['OBR']['OBR.2']['OBR.2.1'] = "T20060810.000001.01";
					inHL7['OBR']['OBR.2']['OBR.2.3'] = "2.16.840.1.113883.3.1863:9999";
					inHL7['OBR']['OBR.2']['OBR.2.4'] = "ISO";

					//get non-profile tests
					if (entryDiagnosticOrderRes['item'][x]['code']['extension']) //https://api.excelleris.com/1.0/eorder/diagnosticorder-item-code-mnemonic
					{
						logger.debug("----get non-profile tests: " + entryDiagnosticOrderRes['item'][x]['code']['coding'][0]['code']);
						inHL7['OBR']['OBR.4']['OBR.4.1'] = entryDiagnosticOrderRes['item'][x]['code']['coding'][0]['code']; //test code ex:27156
						inHL7['OBR']['OBR.4']['OBR.4.2'] = entryDiagnosticOrderRes['item'][x]['code']['text']; //desc ex: SODIUM
						inHL7['OBR']['OBR.4']['OBR.4.3'] = entryDiagnosticOrderRes['item'][x]['code']['extension'][0]['valueString']; //mnemonic ex: NA
					}
					//get profile tests
					else if (entryDiagnosticOrderRes['item'][x]['code']['coding'][0]['extension'])
					{
						if (entryDiagnosticOrderRes['item'][x]['code']['coding'][0]['extension'][0]['url'].toString().trim() == "https://api.excelleris.com/1.0/eorder/diagnosticorder-item-code-coding-profileMnemonic")
						{ 
							logger.debug("--------get profile tests");
							inHL7['OBR']['OBR.4']['OBR.4.1'] = entryDiagnosticOrderRes['item'][x]['code']['coding'][0]['code']; //test code ex:27156
							inHL7['OBR']['OBR.4']['OBR.4.2'] = entryDiagnosticOrderRes['item'][x]['code']['text']; //desc ex: SODIUM
							inHL7['OBR']['OBR.4']['OBR.4.3'] = entryDiagnosticOrderRes['item'][x]['code']['coding'][0]['extension'][0]['valueString']; //mnemonic ex: NA
						}
					}

					//obr-7 observation date time 
					//logger.debug("OBR7");
					inHL7['OBR']['OBR.7']['OBR.7.1'] =  GetHL7DateFromISO(extCollectionDate, "yyyyMMddHHmmssZZZZ"); 
					//inFHIR could contain "M" or "PP"
					inHL7['OBR']['OBR.23']['OBR.23.1'] = entryDiagnosticOrderRes['item'][x]['extension'][0]['valueString']; //https://api.excelleris.com/1.0/eorder/diagnosticorder-item-payertype

					//from practitioner resource
					if (PractitionerRes.length >= 1)
					{
						for each(entryPractitionerRes in PractitionerRes)
						{
							if (entryPractitionerRes['practitionerRole']){
							if (entryPractitionerRes['practitionerRole'][0]['role']['coding'][0]['code'] == "doctor")
							{
								var practitionerID = GetPractitionerID(PractitionerRes, entryDiagnosticOrderRes.orderer.reference);
								if (practitionerID)
								{
									inHL7['OBR']['OBR.16']['OBR.16.1'] = practitionerID;
								}
								var practitionerName = GetPractitionerName(PractitionerRes, entryDiagnosticOrderRes.orderer.reference);
								if (practitionerName)
								{	
									inHL7['OBR']['OBR.16']['OBR.16.2'] = practitionerName.family;
									inHL7['OBR']['OBR.16']['OBR.16.3'] = practitionerName.given;
									inHL7['OBR']['OBR.16']['OBR.16.6'] = practitionerName.prefix?practitionerName.prefix:"";
									inHL7['OBR']['OBR.16']['OBR.16.13'] = "MDL";
								}

								//assigning jurisdiction
								inHL7['OBR']['OBR.16']['OBR.16.22']['OBR.16.22.1'] = "BC";
								inHL7['OBR']['OBR.16']['OBR.16.22']['OBR.16.22.2'] = "BRITISH COLUMBIA";
								inHL7['OBR']['OBR.16']['OBR.16.22']['OBR.16.22.3'] = "HL70347";

								//test level claim type M,A,PP,O intRlab
								//test status: ',O,I,P,A,F,C,X,E'
								inHL7['OBR']['OBR.25']['OBR.25.1'] = GetTestStatus("I");								

							}
							}
						}
					}//end of practitioner 

					//assign extensions to HL7	
					//quantity/timing - required
					var frequency = entryDiagnosticOrderRes.extension.getByUrl("https://api.excelleris.com/1.0/eorder/diagnosticorder-standingorderfrequency");
					if (frequency) 
					{
						inHL7['OBR']['OBR.27']['OBR.27.1']['OBR.27.1.1'] = frequency.valueString;
					} else
					{
						inHL7['OBR']['OBR.27']['OBR.27.1']['OBR.27.1.1'] = "1";	
					}

					inHL7['OBR']['OBR.27']['OBR.27.4'] =  GetHL7DateFromISO(extServiceDate, "yyyyMMddHHmmssZ");
					var expiry = entryDiagnosticOrderRes.extension.getByUrl("https://api.excelleris.com/1.0/eorder/diagnosticorder-standingorderexpirydate")
					if (expiry){
						inHL7['OBR']['OBR.27']['OBR.27.5'] = GetHL7DateFromISO(expiry.valueDateTime, "yyyyMMddHHmmssZ");
					}

					//priority: R, SN, SF, SFN, RN, RFN, RF, RFN
					if (entryDiagnosticOrderRes['item'][x]['extension'][1].url == "https://api.excelleris.com/1.0/eorder/diagnosticorder-item-priority")
					{
						//logger.debug("....Found item priority..." + entryDiagnosticOrderRes['item'][x]['extension'][1].valueString)
						for each(itemContactPoint in entryDiagnosticOrderRes['item'][x]['extension'])
						{
							if (itemContactPoint.url == "https://api.excelleris.com/1.0/eorder/eorder-item-contactpoint")
							{
								//logger.debug("....found item contact point...: " + JSON.stringify(itemContactPoint.valueContactPoint)); 
								inHL7['OBR']['OBR.27']['OBR.27.6'] = GetTestPriorityContactPoint(entryDiagnosticOrderRes['item'][x]['extension'][1].valueString, itemContactPoint.valueContactPoint.system);
							}
						}
					}
					//copy to patient - OBR-28 repeats.
					logger.debug("Create this many OBR-28 copy to segements: " + cntCopyTo);
					var repeatingOBR          = "";
					var foundPatient 	      = false;
					var firstPractitionerLoop = false;

					//do we have copyto patients?
					if (extCopyToPatientUID)
					{
						logger.debug("YES we have PATIENTS to Copy To");

						foundPatient = true;
						//copy to patient
						repeatingOBR += entryPatientRes['identifier'][0]['type']['coding'][0]['code'] + "^"; //OBR-28.1
						repeatingOBR += entryPatientRes['name'][0]['family'] + "^"; //OBR-28.2
						repeatingOBR += entryPatientRes['name'][0]['given'] + "^"; //OBR-28.3
						repeatingOBR += "^"; //OBR-28.4
						repeatingOBR += "^"; //OBR-28.5
						repeatingOBR += "^"; //OBR-28.6
						repeatingOBR += "^"; //OBR-28.7
						repeatingOBR += "^"; //OBR-28.8
						repeatingOBR += "^"; //OBR-28.9
						repeatingOBR += "^"; //OBR-28.10
						repeatingOBR += "^"; //OBR-28.11
						repeatingOBR += "^"; //OBR-28.12
						repeatingOBR += "ANON" + "^"; //OBR-28.13
						repeatingOBR += "^^^^^^^^"; //OBR-28.15 thru OBR-28.21
						repeatingOBR += "BC&BRITISH COLUMBIA&HL70347"; //OBR-28.22
					}//end of patient copyto

					//do we practitioner copyto ?
					if (extCopyToRecipientUIDs.length > 0)
					{
						if (foundPatient == true)
						{
							repeatingOBR+= "~";
							cntCopyTo = cntCopyTo - 1; 
						}

						 //
						 //loop through and create repeating OBR.28 field
						for (var cnt = 0; cnt <  cntCopyTo; cnt++)
						{
							//copy to practitioners
							var practitionerID = GetPractitionerID(PractitionerRes, extCopyToRecipientUIDs[cnt]);
							if (practitionerID)
							{
								repeatingOBR += practitionerID + "^"; //OBR-28.1
							}
								var practitionerName = GetPractitionerName(PractitionerRes, extCopyToRecipientUIDs[cnt]);
								if (practitionerName)
								{
									repeatingOBR += practitionerName.family + "^"; //OBR-28.2
									repeatingOBR += practitionerName.given + "^"; //OBR-28.3
									repeatingOBR += "^"; //OBR-28.4
									repeatingOBR += "^"; //OBR-28.5
									repeatingOBR += (practitionerName.prefix?practitionerName.prefix:"") + "^"; //OBR-28.6
									repeatingOBR += "^"; //OBR-28.7
									repeatingOBR += "^"; //OBR-28.8
									repeatingOBR += "^"; //OBR-28.9
									repeatingOBR += "^"; //OBR-28.10
									repeatingOBR += "^"; //OBR-28.11
									repeatingOBR += "^"; //OBR-28.12
									repeatingOBR += "MDL" + "^"; //OBR-28.13
									repeatingOBR += "^^^^^^^^"; //OBR-28.15 thru OBR-28.21
									repeatingOBR += "BC&BRITISH COLUMBIA&HL70347"; //OBR-28.22
								}
								repeatingOBR+= "~";
						}
					} //end of  practitioner copy to


					//make sure the repeating field doesn't end with repeating char
					//causes issue with empty OBR.28 in repeating.
					if (repeatingOBR.charAt(repeatingOBR.length-1) == "~")
					{
						//logger.debug("Found repeating at end...remove! " + 	repeatingOBR);
						repeatingOBR = repeatingOBR.substring(0, repeatingOBR.length - 1);
					}

					//assign repeating field values to OBR-28
					inHL7['OBR']['OBR.28'] = repeatingOBR;



					//grab current segment
					curORMXML.appendChild(new XML(inHL7['OBR']));
					//logger.debug("OBR added");

					//ZBR -  required Observeration Request Extension Segment
					//test request placer
					inHL7['ZBR']['ZBR.2']['ZBR.2.1'] = "LIFELABS";
					inHL7['ZBR']['ZBR.2']['ZBR.2.6']['ZBR.2.6.2'] = "2.16.840.1.113883.3.1863:9999";
					inHL7['ZBR']['ZBR.2']['ZBR.2.6']['ZBR.2.6.3'] = "ISO";

					//default/accesion level claim type: M,A,PP,O
					inHL7['ZBR']['ZBR.13'] = extPayerType;

					if (entryDiagnosticOrderRes.priority)
					{
						//logger.debug("found default priority type: " + entryDiagnosticOrderRes.priority);
						//deault/accession level priority: R,RF,RFN,RN,SF,SFN,SN

						if (extContactPointSystem.length == 0)
						{
							inHL7['ZBR']['ZBR.14'] = "R";
						}
						else if (extContactPointSystem.length > 0)
						{
							inHL7['ZBR']['ZBR.14'] = GetTestPriorityContactPoint(entryDiagnosticOrderRes.priority, extContactPointSystem);	
						}

					}
					else
					{
						inHL7['ZBR']['ZBR.14'] = "R";
					}

					//Only map ZBR.16 Priority Instructions If Fax or Notify priority is indicated in ZBR.14
					if (inHL7['ZBR']['ZBR.14'] != "R")
					{

						if (extContactPointSystem.length > 0 && 
						    extContactPointSystem.toUpperCase() == "PHONE")
						{
							inHL7['ZBR']['ZBR.15'] = "N1";
							inHL7['ZBR']['ZBR.16']['ZBR.16.1'] = "N1";
						}
						else if (extContactPointSystem.length > 0 && 
						    extContactPointSystem.toUpperCase() == "FAX")
						{
							inHL7['ZBR']['ZBR.15'] = "F1";
							inHL7['ZBR']['ZBR.16']['ZBR.16.1'] = "F1";
						}


						//Practitioner Name. 
						var PractitionerName = GetPractitionerName(PractitionerRes, entryDiagnosticOrderRes.orderer.reference);
						if (PractitionerName)
						{
							inHL7['ZBR']['ZBR.16']['ZBR.16.2'] =  (PractitionerName.prefix?PractitionerName.prefix+ " ":"")   + PractitionerName.given + " " + PractitionerName.family; 
						}

						//
						//zbr-16.3 - 16.6 ITEM level valueContactPoint.
						//
						for each(itemTest in entryDiagnosticOrderRes.item[x].extension)
						{
							if (itemTest.url == "https://api.excelleris.com/1.0/eorder/eorder-item-contactpoint")
							{
								inHL7['ZBR']['ZBR.16']['ZBR.16.3'] = (itemTest.valueContactPoint.value != undefined) ? itemTest.valueContactPoint.value : "";
								inHL7['ZBR']['ZBR.16']['ZBR.16.4'] = GetTestLevelSystem(itemTest.valueContactPoint.system);

								for each(valuePointExt in itemTest.valueContactPoint.extension)
								{
									if (valuePointExt.url == "https://api.excelleris.com/1.0/eorder/eorder-item-contactpointInstruction")
									{
										logger.debug("16.5..." + valuePointExt.valueString);
										inHL7['ZBR']['ZBR.16']['ZBR.16.5'] = (valuePointExt.valueString != undefined) ? valuePointExt.valueString : "";
									}
								}
								for each(valuePointExt in itemTest.valueContactPoint.extension)
								{
									if (valuePointExt.url == "https://api.excelleris.com/1.0/eorder/eorder-item-contactpointAfterhoursphone")
									{
										logger.debug("16.6..." + valuePointExt.valueString);
										inHL7['ZBR']['ZBR.16']['ZBR.16.6'] = (valuePointExt.valueString != undefined) ? valuePointExt.valueString : "";
									}
								}

							}
						}//end of valueContactPoint loop

					}				
					inHL7['ZBR']['ZBR.17'] = extSupplementalLabels;
					inHL7['ZBR']['ZBR.18'] = GetEHealthLabel(extMyEHealth);

					//will contain actual codes
					inHL7['ZBR']['ZBR.19'] = extStatTransportMode;

					if (extStatETA)
					{
						inHL7['ZBR']['ZBR.20'] = GetHL7DateFromISO(extStatETA, "yyyyMMddHHmmssZZZZ");
					}
					inHL7['ZBR']['ZBR.21'] = extStatSpun;


					//grab current segment
					curORMXML.appendChild(new XML(inHL7['ZBR']));
					//logger.debug("ZBR added");

					//Item Level NTE/ZNT pair         


				logger.debug(entryDiagnosticOrderRes.item[x].code.text);
				//if a canned comment was used then pass that in NTE.3, otherwise just send the free text
				function isNote(value){					
					return value.url == "https://api.excelleris.com/1.0/eorder/StructureDefinition/Note";
				}
				var  itemNotes = entryDiagnosticOrderRes['item'][x].extension.filter(isNote);


				for each (note in itemNotes){
					logger.debug("adding item level comment");
				    inHL7['NTE'] = new XML("<NTE></NTE>");
				    inHL7['NTE']['NTE.1']['NTE.1.1'] = 1;
				    inHL7['NTE']['NTE.2']['NTE.2.1'] = "L";	

				    var theValueAnnotation = note.valueAnnotation;
				    //use the canned comment code if you have one otherwise use the annotation text
				    if (theValueAnnotation.extension.getByUrl("https://api.excelleris.com/1.0/eorder/StructureDefinition/CannedComment"))
				    {
				    	   inHL7['NTE']['NTE.3']['NTE.3.1'] = theValueAnnotation.extension.getByUrl("https://api.excelleris.com/1.0/eorder/StructureDefinition/CannedComment").valueCode.toUpperCase();
				    } else {
				    	   inHL7['NTE']['NTE.3']['NTE.3.1'] = theValueAnnotation.text.replace(/\r\n/g, '\\.br\\').toUpperCase();
				    }
				    inHL7['NTE']['NTE.4']['NTE.4.1'] = "RE";
				    inHL7['NTE']['NTE.4']['NTE.4.2'] = "REMARKS";
				    inHL7['NTE']['NTE.4']['NTE.4.3'] = "LIFELABS";

				    inHL7['ZNT'] = new XML("<ZNT></ZNT>");
				    //grab current segment
				    curORMXML.appendChild(new XML(inHL7['NTE']));

				    //grab current segment
				    curORMXML.appendChild(new XML(inHL7['ZNT']));
				} 	


					//
					//OBX - Observation 
					//ZBX - Observation Extension
					//
					//reset pre-reportable counter
					cntPreReportables = 1;

					//get pre-reportables
					for each(prereportableRef in entryDiagnosticOrderRes.item[x].extension)
					{
						//logger.debug("found extension array. url: " + JSON.stringify(url)); 
						if (prereportableRef.url == "https://api.excelleris.com/1.0/eorder/diagnosticorder-item-supportinginformation")
						{
							inHL7['OBX']['OBX.1']['OBX.1.1'] = cntPreReportables;
							inHL7['ZBX'] = "|";
							if (prereportableRef.valueReference)
							{
								//go through contained and find details that belongs to refPrereportableID 
								for each (prereportable in entryDiagnosticOrderRes.contained)
								{
									//logger.debug("contained id: " + JSON.stringify(prereportable.id));
									if (prereportable.id == prereportableRef.valueReference.reference)
									{
								 		for each(coding in prereportable.code)
								 		{
								 			//logger.debug("found code! " + JSON.stringify(coding[0].code));
								 			//logger.debug("found display! " + JSON.stringify(coding[0].display));
								 			inHL7['OBX']['OBX.3']['OBX.3.1'] = coding[0].code;
											inHL7['OBX']['OBX.3']['OBX.3.2'] = coding[0].display;
								 		}

										//inHL7['OBX']['OBX.3']['OBX.3.1'] = "coding";
										//inHL7['OBX']['OBX.3']['OBX.3.2'] = "display";

										inHL7['OBX']['OBX.3']['OBX.3.3'] = "HL7902";
										inHL7['OBX']['OBX.5']['OBX.5.1'] = prereportable.valueString;
										inHL7['OBX']['OBX.11'] 		   = "F";
										inHL7['OBX']['OBX.14']           = GetHL7Date(new Date().toISOString());
										cntPreReportables++;
									}
								}
								//grab current segment
								curORMXML.appendChild(new XML(inHL7['OBX']));
								curORMXML.appendChild(new XML(inHL7['ZBX']));
							}
						}	
					}


					//BLG -billing
					//https://api.excelleris.com/1.0/eorder/diagnosticorder-item-payertype" extension is required.  If it is missing the mapper will 
					if (entryDiagnosticOrderRes.item[x].extension.getByUrl("https://api.excelleris.com/1.0/eorder/diagnosticorder-item-payertype").valueString.match(/(^|\W)(C|CF|D|ICBC|RCMP|REF|W|A)($|\W)/)){
						logger.debug("looking for other ID");
						inHL7['BLG']['BLG.3']['BLG.3.1'] = entryDiagnosticOrderRes.item[x].extension.getByUrl("https://api.excelleris.com/1.0/eorder/diagnosticorder-item-otheridnum").valueString;
					} else {
					 	inHL7['BLG']['BLG.3']['BLG.3.1'] = "SELF";
					}


					curORMXML.appendChild(new XML(inHL7['BLG']));
					//logger.debug("BLG added");

				}//end of test loop
			}//end of diagnosticorder loop

		}//end of diagnostic 
		}//end of ORC,OBR,OBX

	//return HL7 ORM
	logger.debug("curORMXML:" + SerializerFactory.getSerializer('HL7V2').fromXML(curORMXML).toString());	
	return SerializerFactory.getSerializer('HL7V2').fromXML(curORMXML);
}
catch(err)
{
	logger.error("PROBLEM with converting to HL7:" + err);
	throw(err);
}	
}

function getAction()
{

	var action = "";
	var httpRequestMethod = globalMap.get('httpRequestMethod');

	var action = {
		'PUT': function(){ 
		 	return "XO";
		 },
		 'DELETE': function(){
		 	return "CA";
		 },
		 'Default': function(){
		 	return "NW";
		 }
	};

	logger.debug( typeof(httpRequestMethod));
	logger.debug("getAction:" + httpRequestMethod);

	logger.debug(httpRequestMethod.toString() == 'DELETE');

	if (action[httpRequestMethod]){
	    return action[httpRequestMethod]();
	} else {
	   return action.Default();
	}

}

/**
	FHIR Translations.

	@param {Any} inPID8 - 
	@return {String} return FHIR code
*/
//https://www.hl7.org/fhir/2015May/administrative-gender.html
function GetFHIRGender(inPID8)
{
	if (inPID8.toString().toUpperCase().trim() == "M")
	{
		 return "male";
	}
	else if (inPID8.toString().toUpperCase().trim() == "F")
	{
		return "female";
	}
	else if (inPID8.toString().toUpperCase().trim() == "U")
	{
		return "unknown";
	}

	return "unknown";
}

//http://hl7.org/fhir/valueset-contact-point-system.html
function GetFHIRTelecomSystem(inPID13)
{
	//PID-13.3
	if (inPID13.toString().toUpperCase().trim() == "PH") //Telephone
	{
		return "phone";
	}
	else if (inPID13.toString().toUpperCase().trim() == "FX") //Fax
	{
		return "fax";
	}
	else if (inPID13.toString().toUpperCase().trim() == "CP") //Cellular Phone
	{
		return "phone";
	}
	else if (inPID13.toString().toUpperCase().trim() == "BP") //Beeper
	{
		return "page";		
	}
	else if (inPID13.toString().toUpperCase().trim() == "Internet") //Internet Address: Use Only If Telecommunication Use Code Is NET
	{
		return "email";
	}
	return "phone";
}

//http://hl7.org/fhir/valueset-contact-point-use.html
function GetFHIRTelecomUse(inPID13)
{
	//PID-13.2
	if (inPID13.toString().toUpperCase().trim() == "PRN") //Primary Residence Number
	{
		return "home";
	}
	if (inPID13.toString().toUpperCase().trim() == "ORN") //Other Residence Number
	{
		return "home";
	}
	if (inPID13.toString().toUpperCase().trim() == "VHN") //Vacation Home Number
	{
		return "home";
	}
	if (inPID13.toString().toUpperCase().trim() == "WPN") //Work Number
	{
		return "work";
	}
	if (inPID13.toString().toUpperCase().trim() == "ASN") //Answering Service Number
	{
		return "temp";
	}
	if (inPID13.toString().toUpperCase().trim() == "EMR") //Emergency Number
	{
		return "temp";
	}
	if (inPID13.toString().toUpperCase().trim() == "NET") //Network (email) Address
	{
		return "mobile";
	}
	if (inPID13.toString().toUpperCase().trim() == "BPN") //Beeper Number
	{
		return "mobile";
	}
	return "home";
}

//https://www.hl7.org/fhir/valueset-address-use.html
function GetFHIRAddressUse(inPID11)
{
	//PID-11.7
	if (inPID11.toString().toUpperCase().trim() == "M") //Mailing
	{
		return "home";
	}
	if (inPID11.toString().toUpperCase().trim() == "B") //Firm/Business
	{
		return "work";
	}
	if (inPID11.toString().toUpperCase().trim() == "O") //Office
	{
		return "work";
	}
	if (inPID11.toString().toUpperCase().trim() == "H") //Home
	{
		return "home";
	}
	if (inPID11.toString().toUpperCase().trim() == "E") //Emergency Contact Address
	{
		return "temp";
	}
	return "home";

}

//May not need. IntRLabs days deprecated.
function GetFHIRDeathIndicator(inPID30)
{

	if (inPID30.toString().toUpperCase().trim() == "Y") //Yes
	{
		return "true";
	}
	else if (inPID30.toString().toUpperCase().trim() == "N") //No
	{
		return "false";
	}
	return false;

}

//inPID7 = YYYYMMDD
function GetDOB(inPID7)
{	
	var dtMonth = inPID7.substring(4,6);
	var dtDay   = inPID7.substring(6);
	var dtYear  = inPID7.substring(0,4);
	var dtFormatted = dtYear + "-" + dtMonth + "-" + dtDay;

	return dtFormatted;
}

//Converts date ISO 8601
//inPID7 = YYYYMMDD
function GetISODate(inDateStr)
{
	var dtMonth = inDateStr.substring(4,6) - 1;
	var dtDay   = inDateStr.substring(6);
	var dtYear  = inDateStr.substring(0,4);

	//new Date(birth_year, birth_month-1, birth_day, hours, mins, sec);

	//var dtISO = new Date(parseInt(inDateStr));

	var dtISO = new Date(dtYear, dtMonth, dtDay);
	logger.info("HL7 to date: " + dtISO);
	logger.info("HL7 to ISO Date: " + dtISO.toISOString());

	//logger.info("In GetISODate: " + dtISO.toISOString());
	return dtISO;
}

//20090817091500-0400 (-+ZZZZ) is time zone

//ex:  63993.154-63987.61706.182606
function GetTimeFromMSH(inMSH10)
{
	var arrMSH = inMSH10.toString().split("-");

	if(arrMSH.length == 2)
	{
	  return arrMSH[1];
	}

	return "";
}


//@return FHIR name object
function GetPractitionerName(inFHIRResourceArray, inResourceFullURL)
{
	 //logger.debug("In GetPractitionerName searching for ID: " + JSON.stringify(inResourceFullURL));

	try
	{
		for each (resource in  inFHIRResourceArray)
		{
			if (resource.resourceType == "Practitioner")
			{
				if (resource.fullUrl.trim() == inResourceFullURL.trim())
				{
					//logger.debug("FOUND PRACTitioner IN ARRAY. FULL URL: " + JSON.stringify(resource.fullUrl));
					//logger.debug("RETURNING docotor name : " + JSON.stringify(resource.name));
					return resource.name;
				}
			}
		}
		return " ";
	}
	catch(err)
	{
		return " ";
	}
}

//@return practitioner id string
function GetPractitionerID(inFHIRResourceArray, inResourceFullURL)
{
	//logger.debug("In GetPractitionerID searching for ID: " + JSON.stringify(inResourceFullURL));

	try
	{
		for each (resource in  inFHIRResourceArray)
		{
			//logger.debug("FOUNR fullURL: " + JSON.stringify(resource.fullURL));

			if (resource.fullUrl == inResourceFullURL)
			{
				//logger.debug("FOUNR fullURL: " + JSON.stringify(inResourceFullURL));

				if (resource.resourceType == "Practitioner")
				{
					for each(identifier in resource.identifier)
					{
						//logger.debug("identifier: " +  JSON.stringify(identifier));
						if (identifier.use.trim() == "official")
						{
							//logger.debug("FOUND PRACTitioner value " + JSON.stringify(identifier.value));
							return identifier.value;
						}	
					}
				}
			}
		}
		return " ";
	}
	catch(err)
	{
		logger.error("ERROR getting practioner UID in GetPractitionerID(): " + err);
		return " ";
	}
}


//
function GetPatientAddress(inFHIRPatientRes)
{
	try
	{
		for each (resource in  inFHIRPatientRes)
		{
			if (resource.resourceType == "Practitioner")
			{
				for each(address in resource.address)
				{
					if (address.use.trim() == "home")
					{
						return address;
					}
				}
			}
		}
	}
	catch(err)
	{
		logger.error("ERROR getting patient UID in GetPatientAddress(): " + err);

		return " ";
	}
}

function GetFHIRTestPriority(inHL7Priority)
{
	//R   = Routine
	//RF  = Routine Fax
	//RN  = Routine/Notify
	//RFN = Routine Fax and Notify 
	//SF  = Stat/Fax
	//SFN = Stat/Fax and Notify
	//SN  = Stat Notify
	//S   = Stat

	if (inHL7Priority.toString().trim().toUpperCase() == "R")
	{
		return "routine";
	}
	else if (inHL7Priority.toString().trim().toUpperCase() == "SN")
	{
		return "stat order";
	}
	else if (inHL7Priority.toString().trim().toUpperCase() == "SFN")
	{
		return "stat phone";
	}
	else if (inHL7Priority.toString().trim().toUpperCase() == "SF")
	{
		return "stat fax";
	}
	else if (inHL7Priority.toString().trim().toUpperCase() == "RN")
	{
		return "phone"; //RN, RFN
	}
	else if (inHL7Priority.toString().trim().toUpperCase() == "RF")
	{
		return "fax"; //RF, RFN
	}
	else if (inHL7Priority.toString().trim().toUpperCase() == "SN")
	{
		return "stat notify"; //SN, SF, SFN
	}
	else if (inHL7Priority.toString().trim().toUpperCase() == "S")
	{
		return "stat"; //S
	}

	//if all else fails...
	return "routine";
}

//OBR-27.6
function GetFHIRItemTestPriority(inHL7Priority, inArrExtension)
{
	//R   = routine
	//RF  = routine / fax
	//RN  = routine / phone
	//SF  = stat / fax
	//SN  = stat / phone

	//logger.debug("IN GetTestPriorityContactPoint----" + inHL7Priority);

	if (inHL7Priority.toString().trim().toUpperCase() == "R" ||
	    inHL7Priority.toString().trim().toUpperCase() == "RF" ||
	    inHL7Priority.toString().trim().toUpperCase() == "RN")
	{
		inArrExtension[inArrExtension.length] = {"url":"https://api.excelleris.com/1.0/eorder/diagnosticorder-item-priority","valueString":"routine"};
	}
	else if (obrSeg['OBR.27']['OBR.27.6'].toString().trim() != "SF" ||
	         obrSeg['OBR.27']['OBR.27.6'].toString().trim() != "SN")
	{
		inArrExtension[inArrExtension.length] = {"url":"https://api.excelleris.com/1.0/eorder/diagnosticorder-item-priority","valueString":"stat"};
	}

	return inArrExtension;
}

//OBR-27.6
function GetFHIRItemTestContactPoint(inSeg, inArrExtension)
{
	//R   = routine
	//RF  = routine / fax
	//RN  = routine / phone
	//SF  = stat / fax
	//SN  = stat / phone

	//logger.debug("IN GetFHIRItemTestContactPoint----" + inSeg['ZBR.16']);

	//eorder-item-contactpoint
	var arrItemVCPExtension = new Array();
	arrItemVCPExtension[0] = {"url":"https://api.excelleris.com/1.0/eorder/eorder-item-contactpointInstruction", "valueString": inSeg['ZBR.16']['ZBR.16.5']};

	//if HL7 contains second number
	if (inSeg['ZBR.16']['ZBR.16.6'].toString().trim() != "")
	{
		arrItemVCPExtension[1] = {"url":"https://api.excelleris.com/1.0/eorder/eorder-item-contactpointAfterhoursphone", "valueString": inSeg['ZBR.16']['ZBR.16.6']};
	}
	var objItemVCP = {"extension":arrItemVCPExtension };

	if (inSeg['ZBR.16']['ZBR.16.3'].toString().trim().toUpperCase() == "N")
	{
		objItemVCP.system = "phone";
	}
	else if (inSeg['ZBR.16']['ZBR.16.3'].toString().trim().toUpperCase() == "F")
	{
		objItemVCP.system = "fax";
	}

	objItemVCP.value  = inSeg['ZBR.16']['ZBR.16.4'];

	var objItemExtensionCP = new Object();
	objItemExtensionCP.url = "https://api.excelleris.com/1.0/eorder/eorder-item-contactpoint";
	objItemExtensionCP.valueContactPoint = objItemVCP;

	inArrExtension[inArrExtension.length] = objItemExtensionCP;

	return inArrExtension;
}


