//Creates a JSON array with data specific to an order/accession test.
//@param {HL7/XML} inHL7  
//@return {JSON} array
function CreateDiagnoticOrderItemArray(inHL7Segment)
{
	try
	{
	var itemTestsArray = new Object();

	var itemTestsArrayExtension = [
	{"url":"https://api.excelleris.com/1.0/eorder/diagnosticorder-item-payerType", "valueString": "" }, 
	{"url":"https://api.excelleris.com/1.0/eorder/diagnosticorder-item-priority", "valueString":  "" } 
	];
	itemTestsArrayExtension[0]['valueString'] = inHL7Segment['OBR.23']['OBR.23.1']; //mnemonic
	itemTestsArrayExtension[1]['valueString'] = GetFHIRTestPriority(inHL7Segment['OBR.27']['OBR.27.6']); //test code ex:27156

	//item.code.coding
	var itemTestsArrayCoding = [
	{"system":"", "code": ""}
	];
	itemTestsArrayCoding[0]['system'] = "Intrlab";
	itemTestsArrayCoding[0]['code']   = inHL7Segment['OBR.4']['OBR.4.1'];

	var itemTestsArrayCode 	 = new Object();
	itemTestsArrayCode.coding = itemTestsArrayCoding 

	itemTestsArrayCode.extension = [
	{"url":"https://api.excelleris.com/1.0/eorder/diagnosticorder-item-code-mnemonic", "valueString":""}
	];
	itemTestsArrayCode.extension[0]['valueString'] = inHL7Segment['OBR.4']['OBR.4.3']; //mnemonic

	itemTestsArrayCode.text =  inHL7Segment['OBR.4']['OBR.4.2']; //desc ex: SODIUM

	//assign extension and code to main array
	itemTestsArray.extension = itemTestsArrayExtension ;
	itemTestsArray.code = itemTestsArrayCode;

	//return json array	
	return  itemTestsArray;
	}
	catch(err)
	{
		logger.debug("Error in Code Template (FHIR Translation) in CreateDiagnoticOrderItemArray() function: " + err);
	}
}

/**
	Get a Specific Resource from a FHIR Bundle.

	@param {JSON}   inFHIR - FHIR object (JSON). 
	@param {string} inResourceName - Name of resource to find.
	@return {array} return array of resources found. (could be more than one).
*/
//
// @param {JSON}   inResource - FHIR object (JSON).
// @param {string} inResourceName - Name of resource to find.
function GetResourceFromBundle(inFHIR, inResourceName)
{
	var BundleRes 	= new Array();
	var cntRes	= 0;

	for each (seg in inFHIR) 
	{
		//logger.debug("resource:"  + JSON.stringify(seg));
		//loop through entries
		for each (entries in seg)
		{
			if (entries != null)
			{
				//logger.debug("resource:"  + JSON.stringify(entries.resource));

				//
				// get  resource
				//
				if (entries.resource.resourceType.toString().toUpperCase().indexOf(inResourceName) > -1)
				{
					BundleRes[cntRes] = entries.resource;
					//put fullurl at the end.
					BundleRes[cntRes].fullUrl =  entries.fullUrl;
					//logger.debug("Found " +  inResourceName + " resource: " + JSON.stringify(BundleRes[cntRes]));
					cntRes++;
				} 
			}

		}

	}
	//logger.debug("Returning array of bundles: " + BundleRes.length);
	return BundleRes;	
}

