//ex: 4015551111
function GetTelecomNumber(inNum, isAreaCode)
{
	var rawNum = inNum.replace(/\D+/g,"");
	//get zip?
	if (isAreaCode)
	{
		return rawNum.substring(0, 3);
	}
	else
	{
		return rawNum.substring(3, 12);
	}

}

//https://www.hl7.org/fhir/2015May/administrative-gender.html
function GetGender(inGender)
{
	if (inGender.toString().toUpperCase().trim() == "MALE")
	{
		 return "M";
	}
	else if (inGender.toString().toUpperCase().trim() == "FEMALE")
	{
		return "F";
	}
	else if (inGender.toString().toUpperCase().trim() == "UNKNOWN")
	{
		return "U"; 
	}

	return "U";
}

//http://hl7.org/fhir/valueset-contact-point-system.html
function GetTelecomSystem(inTelSystem)
{
	if (inTelSystem.toString().toUpperCase().trim() == "PHONE")
	{
		return "PH"; //Telephone
	}
	else if (inTelSystem.toString().toUpperCase().trim() == "FAX") 
	{
		return "FX";//Fax
	}
	else if (inTelSystem.toString().toUpperCase().trim() == "MOBILE")
	{
		return "CP"; //Cellular Phone
	}
	else if (inTelSystem.toString().toUpperCase().trim() == "PAGER") 
	{
		return "BP"; //Beeper		
	}
	else if (inTelSystem.toString().toUpperCase().trim() == "EMAIL")
	{
		return "INTERNET";  //Internet Address: Use Only If Telecommunication Use Code Is NET
	}
	return "PH"; //Telephone
}

