	}
	//if (inTelUse.toString().toUpperCase().trim() == "") 
	//{
	//	return "ASN"; //Answering Service Number 
	//}
	//if (inTelUse.toString().toUpperCase().trim() == "") 
	//{
	//	return "EMR"; //Emergency Number
	//}
	if (inTelUse.toString().toUpperCase().trim() == "MOBILE") 
	{
		return "NET"; //Network (email) Address
	}
	//if (inTelUse.toString().toUpperCase().trim() == "") 
	//{
	//	return "BPN";
	//}
	return "PRN"; //Primary Residence Number
}

