//IntRLab wants YYYYMMDDHHMMSS-ZZZZ
//inDate is ISO 8601 - 1985-07-27T19:30:20.111Z
//return  "yyyyMMddHHmmssZZZZ"
function GetHL7Date(inISODate)
{
	//var objRegExp = /(\d{4})(\d{2})(\d{2})(\d{2})(\d{2})(\d{2})/;
	//var result = inISODate.replace(objRegExp, "$1$2$3$4$5$6-0800");
	return DateUtil.getCurrentDate("yyyyMMddHHmmssZZZZ");
	//return DateUtil.formatDate("YYYYMMDDHHMMSS-ZZZZ", date);
	//DateUtil.convertDate("YYYY-MM-DD-THH:MM:SS.UUUU[+|-ZZzz]", "YYYYMMDDHHMMSS-ZZZZ", date)


	try
	{
	     return "20160219214532-8000";
		var dtISO =  new Date(inISODate);//FHIR = "1985-07-27T19:30:20.111Z"
		//logger.info("dt readable: " + dtISO.toDateString());
		//logger.info("dt incoming: " + connectorMessage.getRawData());

		//ensure month and day are two digits for HL7
		var dtMonth = dtISO.getMonth() + 1;
		    dtMonth = ("0" + dtMonth).slice(-2);
		var dtDay = ("0" + dtISO.getDate()).slice(-2);

		//logger.info("before convert");
		var dtHL7 = dtISO.getFullYear().toString() + dtMonth.toString() + dtDay.toString() + dtISO.getHours().toLocaleString('en-US', {minimumIntegerDigits: 2, useGrouping:false}) + dtISO.getMinutes().toLocaleString('en-US', {minimumIntegerDigits: 2, useGrouping:false}) + dtISO.getSeconds().toLocaleString('en-US', {minimumIntegerDigits: 2, useGrouping:false});
		//logger.info("after convert");

		var offset = dtISO.getTimezoneOffset(), o = Math.abs(offset);
		//var offset = new Date().getTimezoneOffset(), o = Math.abs(offset);
		var dtTimeZone = (offset < 0 ? "+" : "-") + ("00" + Math.floor(o / 60)).slice(-2) + ":" + ("00" + (o % 60)).slice(-2);

		var outHL7Date =  dtHL7 + dtTimeZone.toString().replace(':', '');
		logger.info("HL7 Formatted Date from FHIR: " + dtHL7 + dtTimeZone.toString().replace(':', ''));

		return outHL7Date;
	}
	catch(err)
	{
		logger.info("Code Template Function (GetHL7Date) error converting date: " + inISODate + " " + err);
 		throw(err);
	}
}


// @param {String} inISODate - ISO date in string format.
// @param {String} inFormat - Date format as string (ex: MM-DD-yyyy)
// @return {String} return - outDate either empty or containing converted date
function GetHL7DateFromISO(inISODate, inFormat)
{
	var outDate;
	//logger.debug(" In GetHL7DateFromISO  inISODate: " + inISODate);
	//logger.debug(" In GetHL7DateFromISO  inFormat: " + inFormat);

	try
	{
		if (inISODate != "")
		{
			var dtISODash 	= PutDashInISODate(inISODate);			
			var newDate 	=  new Date(dtISODash);

			if (newDate.toString().toUpperCase() != "INVALID DATE")
			{
				outDate = DateUtil.formatDate(inFormat, new Date(dtISODash));
			}
			else
			{				
				outDate = " ";
			}
			logger.debug(" In GetHL7DateFromISO  outDate: " + outDate);	
		}
		logger.debug("returning outDate GetHL7DateFromISO: " + outDate);
		return outDate;
	}
	catch(err)
	{
		logger.error("in GetHL7DateFromISO: " + err);
		return " ";
	}
}

//inISODate - ex:  20160322T09:01:00-03:00
function PutDashInISODate(inISODate)
{

	try
	{
		//split date and time
		//var inISODate  = "20160322T09:01:00-03:00";
		var arrISODate = inISODate.split("T");
		var dtISO      = arrISODate[0]; //ex: 20160322
		var timeISO    = arrISODate[1]; //ex: 09:01:00-03:00

		//add dashes
		var dtMonth = dtISO.substring(4, 6);
		var dtYear  = dtISO.substring(0, 4);
		var dtDay   = dtISO.substring(6, 8);
		var dtFinal = dtYear + "-" + dtMonth + "-" + dtDay;

		return dtFinal + (timeISO? "T" + timeISO:"");
	}
	catch(err)
	{
		logger.error("Error in PutDashInISODate(): code template" + err)
		return " ";
	}
}
