//OBR-27.6
function GetTestPriority(inFHIRPriority)
{
	//R   = Routine
	//RF  = Routine Fax
	//RN  = Routine/Notify
	//RFN = Routine Fax and Notify 
	//SF  = Stat/Fax
	//SFN = Stat/Fax and Notify
	//SN  = Stat Notify

	if (inFHIRPriority.toString().trim().toUpperCase() == "ROUTINE")
	{
		return "R";
	}
	else if (inFHIRPriority.toString().trim().toUpperCase() == "STAT ORDER")
	{
		return "SN";
	}
	else if (inFHIRPriority.toString().trim().toUpperCase() == "STAT PHONE")
	{
		return "SN";
	}
	else if (inFHIRPriority.toString().trim().toUpperCase() == "STAT FAX")
	{
		return "SF";
	}
	else if (inFHIRPriority.toString().trim().toUpperCase() == "PHONE")
	{
		return "RN"; //RN, RFN
	}
	else if (inFHIRPriority.toString().trim().toUpperCase() == "FAX")
	{
		return "RF"; //RF, RFN
	}
	else if (inFHIRPriority.toString().trim().toUpperCase() == "STAT")
	{
		return "SN"; //SN, SF, SFN
	}

	//if all else fails...
	return "R";
}


//ZBX-14
function GetTestPriorityContactPoint(inFHIRPriority, inFHIRContactPoint)
{
	//R   = Routine
	//RF  = Routine Fax
	//RN  = Routine/Notify
	//SF  = Stat/Fax
	//SN  = Stat Notify

	if (inFHIRPriority.toString().trim().toUpperCase() == "ROUTINE" &&
	    inFHIRContactPoint.toString().trim() == "" )
	{
		return "R";
	}
	else if (inFHIRPriority.toString().trim().toUpperCase() == "ROUTINE" &&
		    inFHIRContactPoint.toString().trim().toUpperCase() == "FAX" )
	{
		return "RF";
	}
	else if (inFHIRPriority.toString().trim().toUpperCase() == "ROUTINE" &&
		    inFHIRContactPoint.toString().trim().toUpperCase() == "PHONE" )
	{
		return "RN";
	}
	else if (inFHIRPriority.toString().trim().toUpperCase() == "STAT" &&
		    inFHIRContactPoint.toString().trim().toUpperCase() == "FAX" )
	{
		return "SF";
	}
	else if (inFHIRPriority.toString().trim().toUpperCase() == "STAT" &&
		    inFHIRContactPoint.toString().trim().toUpperCase() == "PHONE" )
	{
		return "SN";
	}

	//if all else fails...
	return "R";
}

