/**
	Check if IntRLab HL7 Load Has One or Multiple ORMs.

	@param {String} xml - the HL7 response in XML format.
	@return {boolean} return true or false.

	http://www.mirthcorp.com/community/forums/showthread.php?t=6902&highlight=add+node+template+xml
*/
function HasMultipleORMsMessages(xml)
{
	try
	{
		var isMultiORM = false;
		var mshCount = 0;

		//loop through IntRLabs response...
		for each (group in xml.children())
		{
			if (group.toString().indexOf("<MSH>") > -1)
			{
				//the first MSH is the response header
				mshCount++;
				//logger.info("FOUND MSH GROUP: " + group);
			}
		}
		//logger.info("MSH COUNT in response is : " + mshCount);

		if (mshCount > 2)
		{
			isMultiORM = true;
		}

	}
	catch(err)
	{
		//log visual error.
		//responseMap.put('Error in Transformaton to FHIR:', err.toString());
		logger.error("Error in Transformaton to FHIR: ", err.message);
		//throw err;
		//channelMap.put('Error in Transformaton to FHIR: ', err);
	}
	return isMultiORM;
}

/**
	Parameter is a JSON bundle object from a channel.
	Returns boolean if order exists along with order count.

	@param {JSON} inOrderBundle - 
	@return {boolean|string} return ex: true/2
*/
function HasMultipleOrdersInFHIRBundle(inOrderBundle) {

	//logger.debug("In Code Template  HasMultipleOrdersInFHIRBundle: " + JSON.stringify(inOrderBundle)); 
	var cntOrder = 0;

	for each (seg in inOrderBundle) 
	{
		//logger.info("SEG: " + JSON.stringify(seg));
		if (seg.toString().toUpperCase() != "BUNDLE")
		{
			//[{"fullUrl":"urn:uuid:cd7d3525-882e-448c-ba3f-86e2e3fdc7bb","resource":{"resourceType":"Patient","identifier":[{"use":"official","type":{"coding":[{"system":"hcn","code":"9698701704"}]}}],"name":[{"use":"usual","family":["XCLOGIUDICE"],"given":["PLIS"]}],"telecom":[{"system":"phone","value":"619-958-0123","use":"home"}],"gender":"male","birthDate":"19551202","address":[{"use":"home","line":["6531 EDMOND TOWERS"],"city":"Lumby","state":"BC","postalCode":"V1S 8G0","country":"can"}]}},{"fullUrl":"urn:uuid:7459701d-383f-4703-85f3-463729a13eb0","resource":{"resourceType":"Practitioner","identifier":[{"use":"official","value":"895"}],"name":{"family":["BRADFORD"],"given":["MOIRA"],"prefix":["Dr"]},"telecom":[{"system":"phone","value":"6042994388","use":"work"},{"system":"fax","value":"6042991405"}],"address":[{"line":["4349 EAST HASTINGS ST"],"city":"BURNABY","state":"BC","postalCode":"V5C 2J8","country":"Canada"}],"practitionerRole":[{"role":{"coding":[{"system":"role","code":"doctor"}]}}]}},{"fullUrl":"urn:uuid:71c9776e-569b-416d-9e81-2c23469d1d46","resource":{"resourceType":"DiagnosticOrder","extension":[{"url":"DiagnosticOrder/claimType","valueString":"M"},{"url":"DiagnosticOrder/priority","valueString":"Routine"}],"subject":{"reference":"urn:uuid:cd7d3525-882e-448c-ba3f-86e2e3fdc7bb"},"orderer":{"reference":"urn:uuid:7459701d-383f-4703-85f3-463729a13eb0"},"encounter":{"reference":"urn:uuid:02fcfed2-bb05-486f-97a4-9adc6f7d946a"},"item":[{"extension":[{"url":"DiagnosticOrder/item/claimType","valueString":"M"},{"url":"DiagnosticOrder/item/priority","valueString":"Routine"}],"code":{"extension":[{"url":"DiagnosticOrder/item/code/mnemonic","valueString":"IM"}],"coding":[{"system":"Intrlab","code":"1205"}],"text":"BLOOD FILM REVIEW"}},{"extension":[{"url":"DiagnosticOrder/item/claimType","valueString":"M"},{"url":"DiagnosticOrder/item/priority","valueString":"Routine"}],"code":{"extension":[{"url":"DiagnosticOrder/item/code/mnemonic","valueString":"---"}],"coding":[{"system":"Intrlab","code":"1148"}],"text":"---"}},{"extension":[{"url":"DiagnosticOrder/item/claimType","valueString":"M"},{"url":"DiagnosticOrder/item/priority","valueString":"Routine"}],"code":{"extension":[{"url":"DiagnosticOrder/item/code/mnemonic","valueString":"---"}],"coding":[{"system":"Intrlab","code":"1165"}],"text":"---"}},{"extension":[{"url":"DiagnosticOrder/item/claimType","valueString":"M"},{"url":"DiagnosticOrder/item/priority","valueString":"Routine"}],"code":{"extension":[{"url":"DiagnosticOrder/item/code/mnemonic","valueString":"---"}],"coding":[{"system":"Intrlab","code":"1167"}],"text":"---"}}]}},{"fullUrl":"urn:uuid:02fcfed2-bb05-486f-97a4-9adc6f7d946a","resource":{"resourceType":"Encounter","location":[{"location":{"reference":"urn:uuid:67b34dd5-b4d1-4006-88d2-a2c5741607cd"}}]}},{"fullUrl":"urn:uuid:67b34dd5-b4d1-4006-88d2-a2c5741607cd","resource":{"resourceType":"Location","extension":[{"url":"location-group","valueString":"BRL"}],"identifier":[{"value":"523"}]}}]
			//logger.debug("have all entries: " + JSON.stringify(seg));

			//loop
			for each (entries in seg)
			{
				if (entries != null)
				{
					//{"fullUrl":"urn:uuid:cd7d3525-882e-448c-ba3f-86e2e3fdc7bb","resource":{"resourceType":"Patient","identifier":[{"use":"official","type":{"coding":[{"system":"hcn","code":"9698701704"}]}}],"name":[{"use":"usual","family":["XCLOGIUDICE"],"given":["PLIS"]}],"telecom":[{"system":"phone","value":"619-958-0123","use":"home"}],"gender":"male","birthDate":"19551202","address":[{"use":"home","line":["6531 EDMOND TOWERS"],"city":"Lumby","state":"BC","postalCode":"V1S 8G0","country":"can"}]}}
					//logger.debug("entry: " + JSON.stringify(entries));
					//loop
					//for each (resEntry in entries)
					//{

						if (entries.resource.resourceType.toString().toUpperCase().indexOf('DIAGNOSTICORDER') > -1)
						{
							//logger.debug("entries.resource.resourceType.toString(): " + entries.resource.resourceType.toString());
							//logger.debug("entry DiagnosticOrder: " + JSON.stringify(entries));//this the order resource object

							//logger.debug("resourceType: " + entries.resource.resourceType);

							//[{"url":"DiagnosticOrder/claimType","valueString":"M"},{"url":"DiagnosticOrder/priority","valueString":"Routine"}]
							//logger.info("-------" + JSON.stringify(resEntry.extension));
							//logger.info("-------" + JSON.stringify(resEntry.item));

							if (entries.resource.item != null || entries.resource.item != undefined )
							{
								//logger.info("FOUND ITEMS: " + JSON.stringify(resEntry.item));
								//loop
								for each (itemOrder in entries.resource.item)
								{
									//logger.info("found item---: " + JSON.stringify(itemOrder));
									cntOrder++;
								}
							}
						}

					//}

				}
			}
		}
	}

	if (cntOrder == 1)
	{
		logger.info("Bundle does NOT have multiple tests:" + cntOrder);
		return false + "|" + cntOrder;
	}

	logger.info("Bundle does DOES have multiple tests: " +  cntOrder);
	//requestor needs to know how many orders are in bundle
	//send back count plus true/false
	//ex: false|1  (one order)
	//ex: true|3 (three orders)
	return true + "|" + cntOrder;
}

/**
	Set/Assign a Specific Resource and fullURL in a FHIR Bundle.

	@param {JSON}   inFHIR - 
	@param {string} inResourceName - 
	@return {JSON} return updated FHIR Bundle
*/
function SetResourceInBundle(inFHIR, inFHIRResouce, inUUID) {

	//logger.debug("In SetResourceInBundle. Adding" + inFHIRResouce);

	inFHIR.entry[inFHIR.entry.length] 		      = inFHIRResouce;
	inFHIR.entry[inFHIR.entry.length-1].fullUrl   =  inUUID;

	return inFHIR;	
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function create_UUID(){
    var dt = new Date().getTime();
    var uuid = 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
        var r = (dt + Math.random()*16)%16 | 0;
        dt = Math.floor(dt/16);
        return (c=='x' ? r :(r&0x3|0x8)).toString(16);
    });
    return uuid;
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function getGender(arg1) {
  if (arg1 == "M" ) {
    return  "male"
  } else {
    return "female"
  }
}

/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function getMarital(arg1) {
  if (arg1 == "S" ) {
    return  "Never Married"
  } else if (arg1 == "M" ){
    return "Married"
  } else if (arg1 == "D" ){
    return "Divorced"
  }
}

var utilsRabbit = (function () {
    importPackage(Packages.com.rabbitmq.client);

    /**
     * Crea una ConnectionFactory configurada para RabbitMQ
     * @param {string} host - Direccin del servidor RabbitMQ (ej: "localhost")
     * @param {number} port - Puerto del servidor RabbitMQ (ej: 5672)
     * @param {string} user - Nombre de usuario
     * @param {string} password - Contrasea del usuario
     * @returns {ConnectionFactory} Objeto ConnectionFactory configurado
     * @throws {Error} Si falla la creacin del factory
     */
    function createConnectionFactory(host, port, user, password) {
        try {
            var factory = new ConnectionFactory();
            factory.setHost(host);
            factory.setPort(port);
            factory.setUsername(user);
            factory.setPassword(password);
            return factory;
        } catch (e) {
            throw new Error(" Error creando ConnectionFactory: " + e);
        }
    }

    /**
     * Declara una cola si no existe previamente
     * @param {Channel} channel - Canal de comunicacin RabbitMQ
     * @param {string} queueName - Nombre de la cola
     * @throws {Error} Si falla la declaracin de la cola
     */
    function declareQueue(channel, queueName) {
        try {
            var durable = true;
            var exclusive = false;
            var autoDelete = false;
            var args = null;
            channel.queueDeclare(queueName, durable, exclusive, autoDelete, args);
        } catch (e) {
            throw new Error(" Error declarando la cola '" + queueName + "': " + e);
        }
    }

    /**
     * Lee un solo mensaje de la cola especificada (modo polling)
     * @param {Channel} channel - Canal RabbitMQ vlido
     * @param {string} queueName - Nombre de la cola
     * @returns {string|null} Mensaje recibido o null si no hay mensaje
     * @throws {Error} Si falla la lectura del mensaje
     */
    function readSingleMessage(channel, queueName) {
        try {
            var response = channel.basicGet(queueName, true); // autoAck = true
            if (response != null) {
                return new java.lang.String(response.getBody(), "UTF-8");
            }
            return null;
        } catch (e) {
            throw new Error(" Error leyendo mensaje de la cola '" + queueName + "': " + e);
        }
    }

    /**
     * Cierra silenciosamente canal y conexin RabbitMQ (no lanza excepciones)
     * @param {Connection|null} connection - Conexin RabbitMQ
     * @param {Channel|null} channel - Canal de comunicacin
     */
    function closeQuietly(connection, channel) {
        try {
            if (channel != null) channel.close();
            if (connection != null) connection.close();
        } catch (e) {
            logger.error(" Error al cerrar conexin/canal: " + e);
        }
    }

    /**
     * Lee un mensaje desde RabbitMQ, conectndose temporalmente al servidor
     * @param {string} host - Direccin del servidor RabbitMQ
     * @param {number} port - Puerto de conexin (ej: 5672)
     * @param {string} user - Usuario de acceso
     * @param {string} password - Contrasea del usuario
     * @param {string} queueName - Nombre de la cola desde donde leer
     * @returns {string|null} Mensaje recibido o null si la cola est vaca
     * @logs Mensajes informativos y errores
     */
    function readFromRabbit(host, port, user, password, queueName) {
        var connection = null;
        var channel = null;

        try {
            var factory = createConnectionFactory(host, port, user, password);
            connection = factory.newConnection();
            channel = connection.createChannel();

            declareQueue(channel, queueName);

            var message = readSingleMessage(channel, queueName);
            if (message !== null) {
                logger.info(" Mensaje recibido de '" + queueName + "': " + message.substring(0,200) + "...");
            } else {
                //logger.info(" No hay mensajes en la cola '" + queueName + "'");
            }

            return message;
        } catch (e) {
            logger.error(" MQ-100 Error general al leer desde RabbitMQ: " + e);
            return null;
        } finally {
            closeQuietly(connection, channel);
        }
    }

    // Exponer solo lo necesario (ms funciones se pueden agregar aqu si lo deseas)
    return {
        readFromRabbit: readFromRabbit
    };
})();


var utilsDatabase = (function () {
    /**
     * Establece una conexin JDBC a PostgreSQL.
     * @param {String} host - Direccin del servidor.
     * @param {String} port - Puerto de conexin (ej: 5432).
     * @param {String} user - Usuario de base de datos.
     * @param {String} password - Contrasea del usuario.
     * @param {String} database - Nombre de la base de datos.
     * @returns {{ connection: java.sql.Connection|null, error: string|null }}
     */
    function GetDatabaseConnection(host, port, user, password, database) {
        logger.info(" GetDatabaseConnection");

        try {
            if (!host || !port || !user || !password || !database) {
                throw " Faltan parmetros de conexin.";
            }

            Packages.java.lang.Class.forName("org.postgresql.Driver");
            var url = "jdbc:postgresql://" + host + ":" + port + "/" + database;
            var DriverManager = Packages.java.sql.DriverManager;
            var connection = DriverManager.getConnection(url, user, password);

            if (connection && !connection.isClosed()) {
                logger.info(" Conexin establecida exitosamente.");
                return { connection: connection, error: null };
            } else {
                var msg = " La conexin fue creada pero est cerrada.";
                logger.error(msg);
                return { connection: null, error: msg };
            }

        } catch (e) {
            var errorMsg = e.javaException ? e.javaException.getMessage() : e.toString();
            logger.error(" Error en GetDatabaseConnection: " + errorMsg);
            return { connection: null, error: errorMsg };
        }
    }

	/**
	 * Reutiliza una conexin si est activa o crea una nueva.
	 * 
	 * @param {java.sql.Connection|null} existingConnection - Conexin existente a reutilizar si est viva.
	 * @param {string} host - Host de la base de datos.
	 * @param {string} port - Puerto.
	 * @param {string} user - Usuario.
	 * @param {string} password - Contrasea.
	 * @param {string} database - Nombre de la base de datos.
	 * @returns {{ connection: java.sql.Connection|null, error: string|null }}
	 */
	function GetOrCreateConnection(existingConnection, host, port, user, password, database) {
	    // logger.info(" GetOrCreateConnection");

	    try {
	        if (existingConnection && typeof existingConnection.isClosed === "function" && !existingConnection.isClosed()) {
	            //logger.info(" Conexin existente reutilizada.");
	            return { connection: existingConnection, error: null };
	        }

	        //logger.info(" Creando nueva conexin...");

	        if (!host || !port || !user || !password || !database) {
	            throw " Faltan parmetros de conexin.";
	        }

	        var result = utilsDatabase.GetDatabaseConnection(host, port, user, password, database);

	        if (result.connection) {
	            logger.info(" Nueva conexin creada exitosamente.");
	        } else {
	            logger.error(" Error al crear nueva conexin: " + result.error);
	        }

	        return result;
	    } catch (e) {
	        var err = e.javaException ? e.javaException.getMessage() : e.toString();
	        logger.error(" Excepcin en GetOrCreateConnection: " + err);
	        return { connection: null, error: err };
	    }
	}

	/**
	 * Cierra una conexin JDBC si est abierta.
	 * 
	 * @param {java.sql.Connection|null} connection - Conexin a cerrar.
	 * @returns {boolean} true si se cerr correctamente o ya estaba cerrada, false si ocurri un error.
	 */
	function CloseConnection(connection) {
	    try {
	        if (connection && typeof connection.isClosed === "function") {
	            if (!connection.isClosed()) {
	                connection.close();
	                logger.info(" Conexin cerrada correctamente.");
	                return true;
	            } else {
	                logger.info(" La conexin ya estaba cerrada.");
	                return true;
	            }
	        } else {
	            logger.warn(" Objeto de conexin invlido o no compatible.");
	            return false;
	        }
	    } catch (e) {
	        logger.error(" Error al cerrar la conexin: " + (e.javaException ? e.javaException.getMessage() : e.toString()));
	        return false;
	    }
	}



    /**
     * Ejecuta un INSERT usando datos en formato JSON.
     * @param {java.sql.Connection} connection - Conexin activa.
     * @param {string} tableName - Tabla objetivo.
     * @param {Object} dataJson - Campos y valores a insertar.
     * @returns {Object} Resultado de la operacin.
     */
	function InsertSqlFromJson(connection, tableName, dataJson) {
	    var PGobject = Packages.org.postgresql.util.PGobject;

	    function setTyped(ps, idx, key, val) {
	        // Soporte para 'col::tipo'
	        var parts = String(key).split("::");
	        var colType = parts.length > 1 ? parts[1].toLowerCase() : null;

	        // nulos
	        if (val === null || typeof val === "undefined") {
	            ps.setObject(idx, null);
	            return;
	        }

	        // Forzar JSON/JSONB por anotacin en clave
	        if (colType === "json" || colType === "jsonb") {

	            var pg = new PGobject();
	            //pg.setType("jsonb"); // "json" o "jsonb"
	            //pg.setValue(typeof val === "string" ? val : JSON.stringify(val));
	            //ps.setObject(idx, pg);
	            //logger.info("entro aca!");
	            //logger.info("tipo de dato");
	            logger.info(typeof val);
	            logger.info(JSON.stringify(val));
	            ps.setObject(idx, JSON.stringify(val), java.sql.Types.OTHER);
	            return;
	        }

	        // Deteccin automtica si es objeto/array -> JSONB por defecto
	        if (typeof val === "object") {
	            var pgAuto = new PGobject();
	            pgAuto.setType("jsonb");
	            pgAuto.setValue(JSON.stringify(val));
	            ps.setObject(idx, pgAuto);
	            return;
	        }

	        // Tipos primitivos
	        if (typeof val === "boolean") {
	            ps.setBoolean(idx, val);
	        } else if (typeof val === "number") {
	            if (Number.isInteger(val)) ps.setLong(idx, val);
	            else ps.setDouble(idx, val);
	        } else {
	            // string u otros
	            ps.setString(idx, String(val));
	        }
	    }

	    try {
	        var keys = Object.keys(dataJson);
	        if (keys.length === 0) throw "El objeto JSON no contiene datos.";

	        // Columnas reales (sin '::tipo')
	        var columns = keys.map(function (k) { return String(k).split("::")[0]; }).join(", ");
	        var placeholders = keys.map(function () { return "?"; }).join(", ");

	        var sql = "INSERT INTO " + tableName + " (" + columns + ") VALUES (" + placeholders + ")";
	        var ps = connection.prepareStatement(sql);

	        for (var i = 0; i < keys.length; i++) {
	            var k = keys[i];
	            var v = dataJson[k];
	            setTyped(ps, i + 1, k, v);
	        }

	        var rowsInserted = ps.executeUpdate();
	        ps.close();
		   logger.info(sql);
	        return JSON.stringify({
	            success: true,
	            message: " Insert realizado correctamente.",
	            rowsAffected: rowsInserted
	        });

	    } catch (e) {
	        logger.error(" Error en InsertSqlFromJson: " + e);
	        return { success: false, error: String(e) };
	    }
	}


    /**
     * Ejecuta un SELECT y retorna un array de objetos.
     * @param {java.sql.Connection} connection - Conexin activa.
     * @param {string} sql - Consulta SELECT.
     * @returns {Array<Object>} Resultados en array.
     */
    function SelectSqlToArray(connection, sql) {
        var results = [];
        var rs = null;

        try {
            rs = connection.executeCachedQuery(sql);
            var metaData = rs.getMetaData();
            var columnCount = metaData.getColumnCount();

            while (rs.next()) {
                var row = {};
                for (var i = 1; i <= columnCount; i++) {
                    row[metaData.getColumnLabel(i)] = rs.getString(i);
                }
                results.push(row);
            }
        } catch (e) {
            logger.error(" Error en SelectSqlToArray: " + e);
        } finally {
            if (rs) rs.close();
        }

        return results;
    }

    /**
     * Ejecuta un SELECT con parmetros y retorna JSON.
     * @param {java.sql.Connection} connection - Conexin activa.
     * @param {string} sql - SQL con placeholders (?).
     * @param {Array} params - Valores de los parmetros.
     * @returns {string} JSON con los resultados.
     */
    function SelectSql(connection, sql, params) {
        var results = [];
        var ps = null;
        var rs = null;

        try {
            ps = connection.prepareStatement(sql);
            for (var i = 0; i < params.length; i++) {
                ps.setString(i + 1, String(params[i]));
            }

            rs = ps.executeQuery();
            var metaData = rs.getMetaData();
            var columnCount = metaData.getColumnCount();

            while (rs.next()) {
                var row = {};
                for (var i = 1; i <= columnCount; i++) {
                    row[metaData.getColumnLabel(i)] = rs.getString(i);
                }
                results.push(row);
            }

            return JSON.stringify(results, null, 2);
        } catch (e) {
            logger.error(" Error en SelectSql: " + e);
            return JSON.stringify({ error: String(e) });
        } finally {
            if (rs) rs.close();
            if (ps) ps.close();
        }
    }

    /**
     * Ejecuta un SELECT y retorna la primera fila como objeto.
     * @param {java.sql.Connection} connection - Conexin activa.
     * @param {string} sql - SQL SELECT.
     * @returns {Object|null} Fila como objeto o null.
     */
    function SelectSqlToObject(connection, sql) {
        var result = null;
        var rs = null;
        var ps = null;

        try {
            ps = connection.prepareStatement(sql);
            rs = ps.executeQuery();
            var metaData = rs.getMetaData();
            var columnCount = metaData.getColumnCount();

            if (rs.next()) {
                result = {};
                for (var i = 1; i <= columnCount; i++) {
                    result[metaData.getColumnLabel(i)] = rs.getString(i);
                }
            }
        } catch (e) {
            logger.error(" Error en SelectSqlToObject: " + e);
        } finally {
            if (rs) rs.close();
            if (ps) ps.close();
        }

        return result;
    }

    /**
     * Ejecuta un UPDATE con data en JSON y tipos opcionales (campo::tipo).
     * @param {java.sql.Connection} connection - Conexin activa.
     * @param {string} tableName - Tabla a actualizar.
     * @param {Object} dataJson - Campos/valores para SET.
     * @param {Object} whereJson - Condiciones del WHERE.
     * @returns {string} Resultado JSON.
     */
	function UpdateSqlFromJson(connection, tableName, dataJson, whereJson) {
	    logger.info(" Ejecutando updateSqlFromJson");

	    // Funcin auxiliar para setear tipos correctamente
	    function setTypedValue(ps, index, val) {
	        if (val === null || typeof val === "undefined") {
	            ps.setNull(index, java.sql.Types.VARCHAR); // o cambia segn tipo esperado
	        } else if (typeof val === "number" && Number.isInteger(val)) {
	            ps.setLong(index, val);
	        } else if (typeof val === "number") {
	            ps.setDouble(index, val);
	        } else if (typeof val === "boolean") {
	            ps.setBoolean(index, val);
	        } else if (typeof val === "object") {
	            //  Evita hstore: convierte a JSON
	            ps.setObject(index, JSON.stringify(val), java.sql.Types.OTHER);
	        } else {
	            ps.setString(index, String(val));
	        }
	    }

	    var ps = null;
	    try {
	        var setKeys = Object.keys(dataJson);
	        var whereKeys = Object.keys(whereJson);
	        if (setKeys.length === 0 || whereKeys.length === 0) {
	            throw " Se requieren campos SET y condiciones WHERE.";
	        }

	        // Soporte para casting explcito con "::tipo"
	        var setClause = setKeys.map(k => {
	            if (k.indexOf("::") > -1) {
	                var parts = k.split("::");
	                return parts[0] + " = ?::" + parts[1];
	            } else {
	                return k + " = ?";
	            }
	        }).join(", ");

	        var whereClause = whereKeys.map(k => k + " = ?").join(" AND ");
	        var sql = "UPDATE " + tableName + " SET " + setClause + " WHERE " + whereClause;

	        logger.info(" SQL generado: " + sql);
	        ps = connection.prepareStatement(sql);

	        // SET
	        var i = 1;
	        for (var k of setKeys) {
	            var cleanKey = k.split("::")[0];
	            setTypedValue(ps, i++, dataJson[k]);
	        }

	        // WHERE
	        for (var k of whereKeys) {
	            setTypedValue(ps, i++, whereJson[k]);
	        }

	        var rowsUpdated = ps.executeUpdate();

	        return JSON.stringify({
	            success: true,
	            message: " Update ejecutado correctamente.",
	            rowsAffected: rowsUpdated
	        }, null, 2);
	    } catch (e) {
	        logger.error(" Error en updateSqlFromJson: " + e);
	        return JSON.stringify({ success: false, error: String(e) });
	    } finally {
	        if (ps !== null) {
	            try {
	                ps.close();
	            } catch (closeErr) {
	                logger.warn(" Error al cerrar PreparedStatement: " + closeErr);
	            }
	        }
	    }
	}


    return {
        GetDatabaseConnection: GetDatabaseConnection,
        InsertSqlFromJson: InsertSqlFromJson,
        SelectSqlToArray: SelectSqlToArray,
        SelectSql: SelectSql,
        SelectSqlToObject: SelectSqlToObject,
        UpdateSqlFromJson: UpdateSqlFromJson,
        GetOrCreateConnection: GetOrCreateConnection,
        CloseConnection: CloseConnection
    };
})();


var utilsConf = (function () {

    /**
     * Verifica si un parmetro est definido en configurationMap o en variables de entorno del sistema.
     * Si lo encuentra, lo copia a globalMap.
     *
     * @param {String} param - Nombre del parmetro a validar (ej: "MQ_HOST").
     * @returns {Boolean} `true` si el parmetro fue encontrado y asignado, de lo contrario lanza error.
     */
    function ValidateParameter(param) {
        if (configurationMap.get(param) != null) {
            globalMap.put(param, configurationMap.get(param));
            logger.info(" Parametro verificado (configurationMap): " + param + " : " + globalMap.get(param));
            return true;
        } else if (java.lang.System.getenv(param) != null) {
            globalMap.put(param, java.lang.System.getenv(param));
            logger.info(" Parametro verificado (System.getenv): " + param);
            return true;
        } else {
            logger.error(" Error en la configuracin de variables: " + param);
            throw "Error de configuracin: " + param;
        }
    }

    /**
     * Carga un conjunto predefinido de variables de entorno en globalMap.
     * Solo copia aquellas que estn definidas (no null).
     *
     * @param {Object} envVars - Objeto con claves internas y claves reales de entorno.
     */
    function LoadEnvironment(envVars) {
        for (var key in envVars) {
            var value = java.lang.System.getenv(envVars[key]);
            if (value != null) {
                globalMap.put(key, value);
                logger.info(" Variable cargada en globalMap: " + key);
            } else {
                logger.warn(" Variable de entorno no definida: " + envVars[key]);
            }
        }
    }

    /**
     * Valida todos los parmetros definidos en el objeto envVars.
     * Usa los valores como nombre real del parmetro de entorno o configuracin.
     *
     * @param {Object} envVars - Objeto con claves internas y nombres reales de las variables.
     * @returns {Boolean} True si todos los parmetros son vlidos, False si alguno falla.
     */
    function ValidateAllParameters(envVars) {
        logger.info(" Validando parmetros definidos externamente");

        for (var key in envVars) {
            var param = envVars[key];
            try {
                var flag = ValidateParameter(param);
                if (!flag) return false;
                // globalMap.put(key, globalMap.get(param)); // Descomentar si se quiere mantener alias distinto
            } catch (e) {
                logger.error(" Fallo al validar '" + param + "': " + e);
                return false;
            }
        }

        logger.info(" Todos los parmetros validados correctamente");
        return true;
    }

    /**
	Crea un array con las constantes provenientes del archivo .env
	*/
	function GetArrayConstants() {
   		const params = [];

		params.push("MQ_HOST");
		params.push("MQ_PORT");
	   	params.push("MQ_USER");
		params.push("MQ_PASSWORD");
		params.push("MQ_QUEUE");
		params.push("TARGET_DB_HOST");
		params.push("TARGET_DB_PORT");
		params.push("TARGET_DB_USER");
		params.push("TARGET_DB_PASSWORD");
		params.push("TARGET_DB_DATABASE");
		params.push("TARGET_FHIR_SERVER");
		// params.push("TARGET_X_API_KEY");	
		params.push("TOKEN_URL");	
		params.push("TOKEN_USER");	
		params.push("TOKEN_PASSWORD");

		return params;
}

    return {
        ValidateParameter: ValidateParameter,
        LoadEnvironment: LoadEnvironment,
        ValidateAllParameters: ValidateAllParameters,
        GetArrayConstants: GetArrayConstants
    };

})();


var utilsFhir = (function () {
    /**
     * Enva una solicitud HTTP tipo POST hacia un servidor FHIR.
     *
     * @param {string} url - URL destino del servidor FHIR.
     * @param {string} method - Mtodo HTTP (actualmente solo POST soportado).
     * @param {object} headers - Encabezados HTTP adicionales.
     * @param {string} body - Contenido JSON del cuerpo de la solicitud.
     * @param {string} token - Token JWT para autenticacin Bearer.
     * @returns {{statusCode: number, body: string}} - Respuesta del servidor.
     */
    function HttpSender(url, method, headers, body, token) {
        var HttpClients = Packages.org.apache.http.impl.client.HttpClients;
        var HttpPost = Packages.org.apache.http.client.methods.HttpPost;
        var StringEntity = Packages.org.apache.http.entity.StringEntity;
        var BasicHeader = Packages.org.apache.http.message.BasicHeader;
        var EntityUtils = Packages.org.apache.http.util.EntityUtils;

        var response = null;

        try {
            var client = HttpClients.createDefault();
            var post = new HttpPost(url);

            // Token de autorizacin
            if (token) {
                post.addHeader(new BasicHeader("Authorization", "Bearer " + token));
            }

            // Encabezados personalizados
            for (var key in headers) {
                post.addHeader(new BasicHeader(key, headers[key]));
            }

            // Contenido del cuerpo
            if (body) {
                post.setEntity(new StringEntity(body, "UTF-8"));
            }

            // Ejecutar solicitud
            response = client.execute(post);
            var statusCode = response.getStatusLine().getStatusCode();
            var entity = response.getEntity();
            var responseBody = entity ? EntityUtils.toString(entity, "UTF-8") : "";

            return {
                statusCode: statusCode,
                body: responseBody
            };

        } catch (e) {
            logger.error(" Error en libHttpSender: " + e.toString());
            return {
                statusCode: 500,
                body: "Error: " + e.toString()
            };
        } finally {
            if (response) response.close();
        }
    }

    /**
     * Verifica si un OperationOutcome contiene errores severos.
     *
     * @param {Object} operationOutcome - Recurso FHIR OperationOutcome parseado.
     * @returns {boolean} - True si contiene algn issue de tipo 'error' o 'fatal'.
     */
    function HasOperationOutcomeError(operationOutcome) {
        logger.info(" Revisando OperationOutcome");

        if (!operationOutcome || operationOutcome.resourceType !== "OperationOutcome") {
            logger.warn(" Recurso invlido o no es un OperationOutcome");
            return false;
        }

        var issues = operationOutcome.issue;
        if (!issues || typeof issues.length === "undefined") {
            return false;
        }

        for (var i = 0; i < issues.length; i++) {
            var severity = issues[i].severity;
            if (severity === "error" || severity === "fatal") {
                return true;
            }
        }

        return false;
    }

    /**
     * Extrae el identifier de un ServiceRequest dentro de un Bundle FHIR.
     *
     * @param {string} bundleJson - Texto JSON del recurso Bundle.
     * @param {string} system - Sistema buscado en los identifier (ej: "http://minsal.cl", etc.).
     * @returns {string|null} - Valor del identifier encontrado o null.
     */
    function ExtractServiceRequestIdentifier(bundleJson, system) {
        try {
            var bundle = JSON.parse(bundleJson);
            if (!bundle.entry || !Array.isArray(bundle.entry)) return null;

            for (var i = 0; i < bundle.entry.length; i++) {
                var resource = bundle.entry[i].resource;
                if (resource && resource.resourceType === "ServiceRequest" && resource.identifier) {
                    for (var j = 0; j < resource.identifier.length; j++) {
                        if (resource.identifier[j].system === system) {
                            return resource.identifier[j].value;
                        }
                    }
                }
            }
        } catch (error) {
            logger.error(" Error procesando Bundle JSON: " + error);
        }

        return null;
    }

    // Exponer funciones pblicas
    return {
        HttpSender: HttpSender,
        HasOperationOutcomeError: HasOperationOutcomeError,
        ExtractServiceRequestIdentifier: ExtractServiceRequestIdentifier
    };
})();


var utilsAuth = (function () {
	function GetToken(username, password, tokenUrl, scope) {
	    var Base64 = Packages.java.util.Base64;
	    var HttpClients = Packages.org.apache.http.impl.client.HttpClients;
	    var HttpPost = Packages.org.apache.http.client.methods.HttpPost;
	    var StringEntity = Packages.org.apache.http.entity.StringEntity;
	    var EntityUtils = Packages.org.apache.http.util.EntityUtils;
	    var ObjectMapper = Packages.com.fasterxml.jackson.databind.ObjectMapper;

	    var response = null;

	    try {
	        // Basic auth header
	        var authString = new java.lang.String(username + ":" + password);
	        var encodedAuth = Base64.getEncoder().encodeToString(authString.getBytes("UTF-8"));
	        var authHeader = "Basic " + encodedAuth;

	        // HTTP client & request
	        var client = HttpClients.createDefault();
	        var post = new HttpPost(tokenUrl);
	        post.setHeader("Authorization", authHeader);
	        post.setHeader("Content-Type", "application/x-www-form-urlencoded");

	        // grant_type + scope opcional
	        var form = "grant_type=client_credentials" + (scope ? "&scope=" + String(scope) : "");
	        post.setEntity(new StringEntity(form, "UTF-8"));

	        response = client.execute(post);
	        var statusCode = response.getStatusLine().getStatusCode();
	        var entity = response.getEntity();
	        var body = entity ? EntityUtils.toString(entity, "UTF-8") : "";

	        // Parse JSON si es posible
	        var token = null;
	        var parsed = null;
	        try {
	            var mapper = new ObjectMapper();
	            parsed = mapper.readValue(body, Packages.java.util.Map);
	            if (parsed && parsed.get) {
	                token = parsed.get("access_token");
	            }
	        } catch (ignore) {
	            // El cuerpo no es JSON o no parseable; se devuelve tal cual en body
	        }

	        // xito: 200 y hay access_token
	        if (statusCode === 200 && token) {
	            return {
	                ok: true,
	                token: token,
	                statusCode: statusCode,
	                body: body,         // por si quieres auditar
	                parsed: parsed      // puede ser null si no era JSON
	            };
	        }

	        // Error semntico (200 sin token) o HTTP != 200
	        var errMsg = (parsed && parsed.get) ? (parsed.get("error_description") || parsed.get("error")) : null;
	        return {
	            ok: false,
	            token: null,
	            statusCode: statusCode,
	            body: body,
	            parsed: parsed,
	            error: errMsg || "Token no presente o error HTTP"
	        };

	    } catch (e) {
	        return {
	            ok: false,
	            token: null,
	            statusCode: 0,
	            body: null,
	            parsed: null,
	            error: e.toString()
	        };
	    } finally {
	        try { if (response) response.close(); } catch (ignore) {}
	    }
	}




    /**
     * Realiza una solicitud HTTP POST con autenticacin Basic para obtener un token OAuth 2.0
     * mediante grant_type=client_credentials.
     *
     * @param {String} username - Nombre de usuario (client_id).
     * @param {String} password - Contrasea del usuario (client_secret).
     * @param {String} tokenUrl - URL del endpoint de autenticacin.
     * @returns {String|null} - Token de acceso o null si hubo error.
     */
    function GetToken2(username, password, tokenUrl) {
        try {
            var authString = new java.lang.String(username + ":" + password);
            var Base64 = Packages.java.util.Base64;
            var encodedAuth = Base64.getEncoder().encodeToString(authString.getBytes("UTF-8"));
            var authHeader = "Basic " + encodedAuth;

            var HttpClients = Packages.org.apache.http.impl.client.HttpClients;
            var HttpPost = Packages.org.apache.http.client.methods.HttpPost;
            var StringEntity = Packages.org.apache.http.entity.StringEntity;
            var EntityUtils = Packages.org.apache.http.util.EntityUtils;
            var ObjectMapper = Packages.com.fasterxml.jackson.databind.ObjectMapper;

            var client = HttpClients.createDefault();
            var post = new HttpPost(tokenUrl);
            post.setHeader("Authorization", authHeader);
            post.setHeader("Content-Type", "application/x-www-form-urlencoded");

            var payload = new StringEntity("grant_type=client_credentials");
            post.setEntity(payload);

            var response = client.execute(post);
            var statusCode = response.getStatusLine().getStatusCode();

            if (statusCode !== 200) {
                logger.error(" Error HTTP al obtener token: " + statusCode);
                response.close();
                return null;
            }

            var entity = response.getEntity();
            var json = EntityUtils.toString(entity);
            logger.info(" Respuesta del servidor: " + json);

            var mapper = new ObjectMapper();
            var map = mapper.readValue(json, Packages.java.util.Map);
            var token = map.get("access_token");

            response.close();
            return token;

        } catch (e) {
            logger.error(" Error en libGetToken: " + e.toString());
            return null;
        }
    }

    // Exponer funciones pblicas
    return {
        GetToken: GetToken
    };
})();
